INSERT INTO `sukien` (`MaSK`, `MaCTGD`, `TieuDe`, `TieuDeKhongDau`, `TomTatSK`, `NoiDung`, `HinhSK`, `SoLuotXem`, `Video`, `created_at`, `updated_at`) VALUES
(1, 1, 'Sự xuất hiện loài người và bầy người nguyên thủy', 'Su-xuat-hien-loai-nguoi-va-bay-nguoi-nguyen-thuy', '<p>-Loài người là do một loài Vượn giống Người (Hominid) cách nay hơn 6 triệu, nhờ trải qua quá trình lao động lâu dài, đến cách nay 4 triệu năm, dần dần chuyển biến thành Người tối cổ (có tác giả gọi là Người thái cổ hay Người thượng cổ). Các nhà khoa học tìm thấy dấu vết của Người tối cổ ở một số nơi như Đông Phi, Inđônêxia, Trung Quốc, Việt Nam.<br>-Họ đi bằng hai chân, đôi tay tự do để sử dụng công cụ, hộp sọ lớn hơn vượn cổ, đã hình thành những trung tâm phát triển tiếng nói trong não .....</p>', '<div class=\"post\"><h2>1.Sự xuất hiện loài người và đời sống bầy người nguyên thủy</h2><h4>Loài vượn cổ (khoảng 6 triệu năm trước)</h4><p>Loài người do một loài vượn giống Người (Hominid) chuyển biến thành.</p><p>Vượn cổ sống khoảng 6 triệu năm trước đây, có thể đứng và đi bằng hai chân, 2 chi trước có thể cầm nắm</p><p>=>Trả qua quá trình lao động và một chặng đường quá độ dài, có tính chất chuyển tiếp, vượn cổ đã chuyển biến thành Người tối cổ.</p><h4>Người tối cổ (4 triệu năm trước đây)</h4><p>Đặc điểm: đã là người, hoàn toàn đi đứng bằng hai chân, đôi tay đã trở nên khéo léo, thể tích sọ não lớn và hình thành trung tâm phát tiếng nói trong não. Tuy nhiên còn đi lom khom, trán thấp và bợt ra sau, u mày cao,…</p><p>Biết chế tạo công cụ, làm ra lửa và kiếm sống bằng lao động tập thể với phương thức hái lượm và săn bắt.</p><p>Quan hệ quần thể của Người tối cổ được gọi là bầy người nguyên thủy.</p><img class=\"img-responsive\" src=\"public/images/noidung/bai1/Nguoi-toi-co-biet-su-dung-lua-trong-sinh-hoat-hang-ngay-World-History.jpg\"/></img><blockquote>Hinh 1.1: Người tối cổ biết sửa dụng lửa trong sinh hoạt hằng ngày</blockquote><h4>Bầy người nguyên thủy</h4><p>Đặc điểm: có người đứng đầu, phân công lao động nam và nữ, cùng chăm sóc con cái.</p><p>Đời sống vật chất và tổ chức xã hội: sống trong hang động, mái đá, theo quan hệ ruột thịt, gồm 5-7 gia đình.</p><p>=>Bầy người nguyên thủy sống trong tình trạng “ăn long ở lỗ”, tự nhiên, bấp bênh.</p><p>Động lực của quá trình chuyển biến từ vượn thành người:</p><p>Do vai trò của quy luật tiến hóa.</p><p>Vai trò của lao động đã tạo ra con người và xã hội loài người.</p><h2>2.Người tinh khôn và óc sáng tạo</h2><p>Khoảng 4 vạn năm trước đây, Người tinh khôn (người hiện đại) đã xuất hiện.</p><p>Người tinh khôn đã có vóc dáng giống như người ngày nay: bàn tay nhỏ khéo léo, hộp sọ và thể tích não phát triển, cơ thể gọn và linh hoạt,… nên còn được gọi là Người hạn đại.</p><p>Bắt nguồn từ quá trình thích ứng lâu dài với những hoàn cảnh tự nhiên khác nhau, ở Người tinh khôn đã xuất hiện những hoàn cảnh tự nhiên khác nhau, ở Người tinh khôn đã xuất hiện những màu da khác nhau, chia thành 3 chủng tộc lớn: da vàng, da đen và da trắng.</p><p>“Óc sáng tạo” là sáng rạo của Người tạo của Người tinh khôn trong việc cải tiến công cụ, nâng cao hiệu quả lao động và sản xuất, trải qua hai quá trình:</p><p>Người tinh khôn từ hậu kì đá cũ đến chế tác đá mới (ghé sắc và mài nhẵn) và biết chế tác thêm công cụ mới: cung tên và lao.</p><img class=\"img-responsive\" src=\"public/images/noidung/bai1/Cac-hinh-thai-tien-hoa-cua-con-nguoi.jpg\"></img><blockquote>Hinh 1.2: Các hình thái tiến hóa của con người</blockquote><h2>3.Cuộc cách mạng đá mới</h2><p>Từ săn bắt, hai lượm, con người đã biết trồng trọt, chăn nuôi, biết khai thác từ thiên nhiên chứ không chỉ thu lượm từ thiên nhiên.</p><p>Làm sạch những tấm da thú để che thân, tìm thấy những chiếc khuy làm bằng xương.</p><p>Biết dùng đồ trang sức như vòng cổ bằng sò ốc, chuỗi hạt xương, vòng tay, vòng cổ chân, hoa tai… bằng đá màu, sáo bằng xương dùi lỗ, đàn đá, trống bịt da.</p><p>=>Con người không ngừng sáng tạo, sống tốt hơn và vui hơn, bớt lệ thuộc vào thiên nhiên.</p><img class=\"img-responsive\" src=\"public/images/noidung/bai1/Cac-cong-cu-bang-da-co-xua-World-History.jpg\"></img><blockquote>Hình 1.3: Các công cụ bằng đá cổ xưa</blockquote><img class=\"img-responsive\" src=\"public/images/noidung/bai1/Riu-da-co-World-History.jpg\"></img><blockquote>Hình 1.4: Rìu đá cổ</blockquote></div>', 'public/images/noidung/bai1/Vuon-co-Nguoi-toi-co-Nguoi-hien-dai-World-History.jpg', 69, NULL, '2018-04-03 17:00:00', NULL),
(2, 1, 'Xã hội nguyên thủy', 'Xa-hoi-nguyen-thuy', '<p>Xã hội nguyên thủy (hay còn gọi là công xã thị tộc) là giai đoạn đầu tiên và dài nhất trong lịch sử phát triển của loài người, từ khi có con người xuất hiện trên trái đất đến khi xã hội phân chia thành các giai cấp và xuất hiện nhà nước.</p><p>Xã hội nguyên thủy phát triển kế thừa lối sống xã hội bầy đàn của linh trưởng tổ tiên, và thể hiện gần gũi nhất hiện có hai loài là tinh tinh và bonobo ở châu Phi. Xã hội nguyên thủy cũng kết thúc khác nhau ở các vùng và dân tộc khác nhau. Nhiều dân tộc ở châu Phi và Nam Mỹ đã có lối sống không thay đổi trong chục nghìn năm qua, và họ như là bảo tàng sống của loài người về thời nguyên thủy, như người Hadza, San,...</p>', '<div class=\"post\"><h2>1.Thị tộc và bộ lạc</h2><p>Thị tộc là nhóm người có khoảng hơn 10 gia đình và có cùng chung một dòng máu. Đứng đầu là tộc trưởng.</p><p>Bộ lạc là tập hợp những thị tộc sống gần nhau sống ở ven song suối, có quan hệ gắn bó với nhau, mọi của cải sinh hoạt được coi là của chung, cùng làm chung, cùng ăn chung, cùng hưởng thụ như nhau… đứng đầu là tù trưởng và tính “cộng đồng” rất cao.</p><p>Trong xã hội nguyên thủy, sự công bằng và bình đẳng là “nguyên tắc vàng”.</p><img class=\"img-responsive\" src=\"public/images/noidung/bai2/Thi-toc-bo-lac-World-History.jpg\"></img><blockquote>Hình 2.1: Người San ở hoang mạc Kalahari</blockquote><h2>2.Buổi đầu của thời đại kim khí</h2><p>Từ chỗ dùng những công cụ bằng đá, gỗ, xương,… con người đã biết chế tạo đồng dùng và công cụ bằng đồng.</p><p>Cư dân Tây Á và Ai Cập biết sử dụng đồng sớm nhất, đồng đỏ - khoảng 5500 năm trước đây.</p><p>Khoảng 4000 năm trước đây nhiều cư dân trên trái đất biết sử dụng đồng thau.</p><p>Khoảng 3000 năm trước đây, cư dân Tây Á và Nam Âu biết dùng đồ sắt.</p><p>Công cụ kim khí đã mở ra thời đại mới: năng suất lao động tăng nhanh, con người đã có thể khai phá thêm những vùng đất mới, đóng thuyền đi biển, xẻ đá làm lâu đài,…</p><p>=>Vào buổi đầu thời đại kim khí con người tạo ra một lượng sản phẩm thừa thường xuyên.</p><img class=\"img-responsive\" src=\"public/images/noidung/bai2/Cong-cu-kim-loai-co-xua-World-History.jpg\"></img><blockquote>Hình 2.2: Công cụ kim loại cổ xưa</blockquote><h2>3.Sự xuất hiện của tư hữu và xã hội có giai cấp</h2><p>Khi xã hội có sản phẩm thừa, một số người lợi dụng chức phận đã chiếm một phẩm của xã hội làm sản phẩm riêng cho mình.</p><p>Tư hữu bắt đầu xuất hiện, quan hệ cộng đồng bắt đầu bị phá vỡ, gia đình thay đổi theo, gia đình phụ hệ xuất hiện.</p><p>Khả năng lao động của các gia đình khác nhau, thúc đẩy sự phân biệt giàu, nghèo.</p><p>=>Sự xuất hiện của tư hữu đã làm thay đổi quan hệ cộng đồng và gia đình, thúc đẩy xã hội nguyên thủy tan rã. Con người đứng trước ngưỡng cửa của xã hội có giai cấp đầu tiên – xã hội cổ đại.</p><img class=\"img-responsive\" src=\"public/images/noidung/bai2/Xa-hoi-chiem-huu-no-le-World-History.jpg\"></img><blockquote>Hình 2.3: Xã hội chiếm hữu nô lệ</blockquote></div>', 'public/images/noidung/bai2/Xa-hoi-nguyen-thuy-World-History.jpg', 5, NULL, '2018-04-03 17:00:00', NULL),
(3, 2, 'Các quốc gai cổ đại phương Đông', 'Cac-quoc-gia-co-dai-phuong-Dong', '<p>Cơ sở hình thành: Sự phát triển của sản xuất dẫn tới sự phân hóa giai cấp, từ đó nhà nước ra đời.</p><p>Các quốc gia cổ đại đầu tiên xuất hiện ở Ai Cập, Lưỡng Hà, Ấn Độ, Trung Quốc, vào khoảng thiên niên kỷ thứ IV- III trước Công nguyên.</p><p>Những tầng lớp trong xã hội cổ đại phương Đông: Vua -> Quý tộc -> Nông dân công xã -> Nô lệ. ...</p>', '<div class=\"post\"><h2>1.Điều kiện tự nhiên và sự phát triển kinh tế</h2><p>Sự xuất hiện của công cụ kim loại, con người vào thời đại văn minh.</p><p>Những quốc gia cổ đại phương Đông đầu tiên hình thành ở lưu vực các dòng sông lớn như:</p><ul><li>Ai Cập: sông Nin.</li><li>Lưỡng Hà: sông Ti-gơ-rơ và sông Ơ-phơ-rát.</li><li>Ấn Độ: sông Ấn và sông Hằng.</li><li>Trung Quốc: sông Hoàng Hà và sông Trường Giang.</li></ul><h4>Các quốc gia cổ đại phương Đông đầu tiên hình thành ở các dòng sông lớn vì:</h4><ul><li>Ở lưu vực các con sông lớn, đất đai màu mỡ, mềm xếp nên công cụ bằng gỗ, đá cũng có thể canh tác được và tạo nên mùa màng bộ thu (điều đó lí giải vì sao nhà nước ở đây hình thành sớm khi chưa có công cụ bằng kim loại).</li><li>Muốn bảo vệ màu màng phải đắp đê, trị thủy và làm thủy lợi. Công việc này vừa đòi hỏi công sức của nhiều người, vừa tạo nên nhu cầu để mọi người sống quần tụ, gắn bó với nhau trong các tổ chức xã hội.</li></ul><p>Sự phát triển các ngành kinh tế: nông nghiệp tưới tiêu, chăn nuôi và thủ công nghiệp, trong đó nông nghiệp tưới tiêu là ngành kinh tế chính tạo ra của cải dư thừa thường xuyên.</p><p>=>Do nhu cầu sản xuất, trị thủy, thủy lợi,... con người đã sống quần tụ và gắn bó với nhau trong tổ chức công xã, nhờ đó nhà nước sớm hình thành.</p><img class=\"img-responsive\" src=\"public/images/noidung/bai3/Xa-hoi-Ai-Cap-co-dai-World-History.jpg\"></img><blockquote>Hình 3.1: Xã hội Ai Cập cổ đại</blockquote><img class=\"img-responsive\" src=\"public/images/noidung/bai3/An-do-co-dai-World-History.jpg\"></img><blockquote>Hình 3.2: Ấn Độ cổ đại</blockquote><h2>2.Sự hình thành các quốc gia cổ đại phương Đông (khoảng thiên nhiên kỷ VI-III TCN)</h2><p>Sản xuất phát triển dẫn đến sự phân hóa xã hội, xuất hiện kẻ giàu, người nghèo nên giai cấp và nhà nước ra đời:</p><ul><li>Thiên nhiên kỷ IV TCN, trên lưu vực sông Nin, cư dân Ai Cập cổ đại sống tập trung theo từng công xã, sau này do nhu cầu làm thủy lợi, công xã hợp lại thành Liên minh công xã (Nôm). Khoảng 3200 năm TCN nhà nước Ai Cập thống nhất trên cơ sở tập hợp các \"Nôm\" được thành lập.</li><li>Ở lưu vực Lưỡng Hà (thiên niên kỉ IV TCN), hàng chục nước nhỏ người Su-me đã hình thành.</li><li>Trên lưu vực sông Ấn, các quốc gia cổ đại ra đời giữa thiên niên kỷ III TCN.</li><li>Vương triều nhà Hạ hình thành vào thế kỷ XXI TCN mở đầu cho xã hội có giai cấp và nhà nước Trung Quốc.</li></ul><p>=>Các quốc gia cổ đại phương Đông đều được hình thành từ rất sớm, khoảng thiên nhiên kỉ IV-III TCN.</p><h2>3.Xã hội cổ đại phương Đông</h2><p>Do nhu cầu thủy lợi, nông dân gắn bó và ràng buộc với nhau trong công xã nông thôn, thành viên trong công xã gọi là nông dân công xã.</p><p>Nông dân công xã: đông đảo nhất, là lao động chính trong sản xuất.</p><p>Quý tộc: gồm quan lại, thủ lĩnh quân sự, tăng lữ có nhiều quyền thế, giữ chức vụ tôn giáo. Họ sống sung sướng dựa trên sự bóc lột nông dân: thu thuế của nông dân dưới quyền trực tiếp hoặc nhận bổng lộc của nhà nước cũng do thu thuế của nông dân.</p><p>Nô lệ: chủ yếu là tù binh và thành viên công xã bị mắc nợ không trả được hoặc bị phạm tội. Họ là tầng lớp thấp nhất trong xã hội, phải làm việc nặng nhọc, hầu hạ quý tộc.</p><img class=\"img-responsive\" src=\"public/images/noidung/bai3/Doi-song-lao-dong-cua-xa-hoi-Ai-Cap-World-History.jpg\"></img><blockquote>Hình 3.3: Đời sống lao động của xã hội Ai Cập</blockquote><h2>4.Chế độ chuyên chế cổ đại</h2><p>Quá trình hình thành nhà nước từ liên minh bộ lạc, do nhu cầu trị thủy và xây dựng các công trình thủy lợi nên quyền hành cần tập trung vào tay nhà vua, tạo nên chế độ quân chủ chuyên chế.</p><p>Vua dựa vào quý tộc và tôn giáo, bắt mọi người phải phục tùng. Vua chuyên chế - người Ai Cập gọi là Pha-ra-ông (cái nhà lớn), người Lưỡng Hà gọi là En-xi (người đứng đầu), Trung Quốc gọi là Thiên tử (con trời).</p><p>Giúp việc cho vua là một bộ máy hành chính quan liêu gồm quý tộc, đứng đầu là Vidia (Ai Cập), Thừa tướng (Trung Quốc), họ thu thuế, xây dựng các công trình như đền tháp, cung điện, đường sá, chỉ huy quân đội.</p><p>=>Chế độ nhà nước do vua đứng đầu, có quyền lực tối cao và một bộ máy hành chính quan liêu giúp việc,... gọi là chế độ chuyên chế cổ đại.</p><h2>5.Văn hóa cổ đại phương Đông</h2><h4>a.Sự ra đời của Lịch pháp và Thiên văn học</h4><p>Lịch pháp và Thiên văn học ra đời sớm nhất, gắn liền với nhu cầu sản xuất nông nghiệp.</p><p>Họ biết sự chuyển động của Mặt trời, Mặt trăng --> Thiên văn --> Nông lịch.</p><p>Một năm có 365 ngày, chia thành tháng, tuần, ngày, mỗi ngày có 24 giờ.</p><h4>b.Chữ viết</h4><p>Người ta cần ghi chép và lưu giữ nên chữ viết ra đời, đây la phát minh lớn của loài người.</p><p>Ban đầu là chữ tượng hình, sau được cách điệu hóa thành nét để diễn tả ý nghĩa của con người gọi là chữ tượng ý.</p><p>Người Ai Cập viết trên giấy Papyrus.</p><img class=\"img-responsive\" src=\"public/images/noidung/bai3/Giay-Papyrus-World-History.jpg\"></img><blockquote>Hình 3.4: Chữ Ai Cập cổ được viết trên giấy Papyrus</blockquote><p>Người Su-me ở Lưỡng Hà dùng cây sậy vót nhọn là bút viết trên những tấm đất sét còn ướt, rồi đem phơi nắng hay nung khô.</p><p>Người Trung Quốc khắc chữ trên xương thú, mai rùa, thẻ tre, dải lụa...</p><img class=\"img-responsive\" src=\"public/images/noidung/bai3/Chu-viet-tren-the-tre-World-History.png\"></img><blockquote>Hình 3.5: Chữ hán được viết trên thẻ tre</blockquote><h4>c.Toán học</h4><img class=\"img-responsive\" src=\"public/images/noidung/bai3/He-so-duoc-su-dung-trong-xa-hoi-An-Do-co-dai.jpg\"></img><blockquote>Hình 3.6: Hệ số được sử dụng trong xã hội Ấn Độ cổ đại</blockquote><p>Ra đời sớm do nhu cầu sản xuất nông nghiệp (tính diện tích ruộng đất sau khi ngập nước, tính trong xây dựng,...)</p><p>Ban đầu chữ số là những kí hiệu đơn giản. Người Ai Cập cổ đại giỏi về hình học, biết số Pi = 3,16; tính diện tích hình tròn, tam giác,...</p><p>Người Lưỡng Hà giỏi về số học, làm phép tính cộng, trừ, nhân, chia.</p><p>Chữ số mà ta dùng ngày nay, kể cả số 0 là thành tựu lớn của người Ấn Độ.</p><p>=>Thành tựu toán học đã để lại nhiều kinh nghiệm cho bước phát triển cao hơn ở đời sau.</p><h4>d.Kiến trúc</h4><p>Phát triển phong phú: Kim tự tháp Ai Cập, đền tháp ở Ấn Độ, thành Ba-bi-lon ở Lưỡng Hà... Đây là những kỳ tích về sức lao động và tài năng sáng tạo của con người.</p><img class=\"img-responsive\" src=\"public/images/noidung/bai3/Kim-tu-thap-Ai-Cap-World-History.jpg\"></img><blockquote>Hình 3.7: Kim tự tháp Ai Cập</blockquote></div>', 'public/images/noidung/bai3/Cac-quoc-gia-co-dai-phuong-Dong-World-History.jpg', 1, NULL, '2018-04-04 17:00:00', NULL),
(4, 5, 'Trung Quốc thời kỳ phong kiến - Phần I', 'Trung-Quoc-thoi-ky-phong-kien-Phan-I', '<p>Năm 221 trước Công nguyên được coi là năm Trung Quốc bắt đầu thống nhất trở thành một đế chế rộng lớn, với vị Hoàng đế Tần Thủy Hoàng cai trị, đánh dấu sự khởi đầu của đế quốc Trung Hoa. Vào thời kỳ này, Tần Thủy Hoàng cho xây dựng Vạn lý trường thành để bảo vệ đất nước khỏi các tộc người phương Bắc. Ông cho thống nhất chữ viết, các đơn vị đo lường và tiền tệ.</p>', '<div class=\"post\"><h2>1.Trung Quốc thời Tần - Hán</h2><p>Thời cổ đại, trên lưu vực sông Hoàng Hà và sông Trường Giang có nhiều quốc gia nhỏ của người Trung Quốc thường gây chiến tranh và thôn tính lẫn nhau.</p><h4>Thời Tần: 221 TCN - 206 TCN</h4><p>Đầu thế kỷ IV TCN, Tần lần lượt tiêu diệt các đối thủ, năm 221 TCN nhà Tần thống nhất Trung Quốc.</p><p>Vua Tần xưng là Tần Thủy Hoàng có quyền hành tuyệt đối, bắt tay vào việc xây dựng chính quyền.</p><p>Chia đất nước thành quận huyện, cử quan Thái thú (ở quận) và huyện lệnh. Thừa tướng đứng đầu quan văn, Thái úy đứng đầu quan võ.</p><p>Có lực lượng quân sự lớn mạnh để duy trì trật tự xã hội, trấn áp các cuộc nổi dậy, tiến hành chiến tranh xâm lược.</p><img class=\"img-responsive\" src=\"public/images/noidung/bai5/Tan-thuy-hoang-World-History.jpg\"></img><blockquote>Hình 5.1: Tần thủy hoàng - người có công lớn thống nhất Trung Quốc</blockquote><p>Nhà Tần tồn tại 15 năm, Lưu Bang lên ngôi lập ra nhà Hán.</p><h4>Nhà Hán: 206 TCN - 220</h4><p>Tiếp tục củng cố bộ máy cai trị, mở rộng hình thức tiến cử.</p><p>Nhà Tần và nhà Hán chiếm vùng thượng lưu sông Hoàng, thôn tính Trường Giang, chiếm phía đông Thiên Sơn, xâm lược Triều Tiên và đất của người Việt cổ.</p><img class=\"img-responsive\" src=\"public/images/noidung/bai5/Nha-han-World-History.jpg\"></img><blockquote>Hình 5.2: Nhà hán 206TCN - 220</blockquote><h2>2.Sự phát triển chế độ phong kiến thời Đường</h2><p>Sau mấy thế kỷ rối ren, Lý Uyên dẹp tan được phe đối lập, đàn áp khởi nghĩa, lên ngôi hoàng đế lập ra nhà Đường (618-907).</p><img class=\"img-responsive\" src=\"public/images/noidung/bai5/Ban-do-thoi-nha-duong-World-History.jpg\"></img><blockquote>Hình 5.3: Bản đồ thời nhà Đường</blockquote><h5>Chế độ phong kiến thời Đường đạt đến đỉnh cao:</h5><p>Kinh tế phát triển toàn diện:</p><ul><li>Thực hiện chế độ quân điền, nông dân thực hiện chế độ nghĩa vụ cho nhà nước theo chế độ tô, dung, điệu.</li><li>Thủ công nghiệp phát triển, các xưởng thủ công gọi là tác phường như luyện sắt, đóng thuyền,...</li><li>Thương nghiệp thịnh đạt, con đường tơ lụa trên đất liền và trên biển được thiết lập, mở rộng.</li></ul><img class=\"img-responsive\" src=\"public/images/noidung/bai5/Con-duong-to-lua-World-History.jpg\"></img><blockquote>Hình 5.4: Con đường tơ lụa</blockquote><img  class=\"img-responsive\" src=\"public/images/noidung/bai5/Van-chuyen-hang-hoa-tren-con-duong-to-lua-World-History.jpg\"></img><blockquote>Hình 5.5: Vận chuyển hàng hóa trên con đường tơ lụa</blockquote><p>Chính trị: bộ máy cai trị phong kiến hoàn chỉnh: cử người thân tín cai quản địa phương, cử người trong họ hay công thần giữ chức Tiết độ sứ trấn ải biên cương mở khoa thi chọn người ra làm quan.</p><p>Tiếp tục chính sách xâm lược: chiếm Nội Mông, Tây Vực, xâm lược Triều Tiên, củng cố chế độ đô hộ ở An Nam, ép Tây Tạng phải thần phục. Nhà Đường trở thành một đế quốc phong kiến phát triển nhất.</p><p>Cuối thời Đường: mâu thuẫn xã hội gay gắt. Đến cuối thể kỷ XIII Trung Quốc rơi vào tay Mông Cổ.</p></div>', 'public/images/noidung/bai5/Trung-Quoc-thoi-phong-kien-phan-I.jpg', 37, 'public/video/Phong-kien-Trung-Quoc-World-History.mp4', NULL, NULL),
(5, 5, 'Trung Quốc xã hội phong kiến - Phần II', 'Trung-Quoc-xa-hoi-phong-kien-Phan-II', '<p>Trải qua hơn 3.000 năm đế chế (khoảng 2000 TCN - 1912 SCN), văn minh Trung Hoa phát triển rực rỡ, với những thành tựu đáng nể, gồm các phát minh vĩ đại như giấy, nghề in, la bàn... Trong thời gian này, có hai nền đế chế trên toàn Trung Quốc phụ thuộc vào các tộc người ngoại tộc, là người Mông Cổ lập nên nhà Nguyên và người Mãn Châu lập nên nhà Thanh.</p>', '<div class=\"post\"><h2>3.Trung Quốc thời Minh, Thanh</h2><p>Cuối thế kỷ XIII, triều Nguyên (1271-1368) được thành lập bởi vua Mông Cổ (Hốt Tất Liệt): thực hiện chính sách áp bức, chia rẽ các dân tộc, nhân dân Trung Quốc đứng lên lật đổ nhà Nguyên.</p><p>Năm 1368 Chu Nguyên Chương lên ngôi Hoàng đế ở Nam Kinh lập ra nhà Minh (1368-1644).</p><p>Về kinh tế: nhà Minh đã thực hiện nhiều biện pháp nhằm khôi phục và phát triển kinh tế.</p><p>=>Mầm mống quan hệ sản xuất tư bản chủ nghĩa xuất hiện (có những công trường thủ công lớn, nhà buôn bán, thành thị nhiều như Bắc Kinh, Nam Kinh).</p><p>Về chính trị:</p><ul><li>Năm 1380 bỏ chức Thừa tướng, Thái úy thay vào đó là các Thượng thư phụ trách các bộ (6 bộ: Lại, Hộ, Lễ, Binh, Hình, Công), hoàn chỉnh bộ máy quan lại.</li><li>Hoàng đế tập trung mọi quyền hành, trực tiếp nắm quân đội.</li><li>Cuối thời Minh mâu thuẫn xã hội gay gắt. Nông dân khởi nghĩa, cuộc nổi dậy của Lí Tự Thành khiến triều Minh sụp đổ.</li></ul><img class=\"img-responsive\" src=\"public/images/noidung/bai6/Bang-do-nha-Minh-World-History.png\"></img><blockquote>Hình 6.1: Bảng đồ lãnh thổ Trung Quốc thời nhà Minh</blockquote><p>Giữa lúc đó một bộ tộc ở phương Bắc Trung Quốc là Mãn Thanh kéo vào đánh bại Lý Tự Thành lập ra nhà Thanh (1644-1911).</p><p>Nhà Thanh thi hành chính sách áp bức dân tộc, người Trung Quốc phải theo phong tục của người Mãn.</p><p>Do chính sách áp bức bóc lột của nhà Thanh nông dân lại khởi nghĩa, lợi dụng nhà Thanh suy yếu, bọn tư bản phương Tây dòm ngó, xâm lược Trung Quốc.</p><img class=\"img-responsive\" src=\"public/images/noidung/bai6/Trieu-dinh-nha-Thanh-Trung-Quoc-World-History.jpg\"></img><blockquote>Hình 6.2: Triều đình nhà Thanh - Trung Quốc</blockquote><h2>4.Văn hóa Trung Quốc thời phong kiến</h2><p>Tư tưởng Nho giáo giữ vai trò quan trọng, là công cụ sắc bén phục vụ cho nhà nước phong kiến tập quyền.</p><img class=\"img-responsive\" src=\"public/images/noidung/bai6/Khong-tu-World-History.jpg\"></img><blockquote>Hình 6.3: Khổng Tử - người khai sáng Nho giáo</blockquote><p>Phật giáo thịnh hành nhất vào thời Đường, Bắc Tống cho xây nhiều chùa, tạc tượng in kinh...</p><img class=\"img-responsive\" src=\"public/images/noidung/bai6/Thieu-lam-tu-Trung-Quoc-World-History.jpg\"></img><blockquote>Hình 6.4: Thiếu lâm tự Trung Quốc</blockquote><p>Bộ Sử ký của Tư Mã Thiên rất nổi tiếng, thời Đường có cơ quan chép sử là Sử quán...</p><p>Thơ Đường đạt đến đỉnh cao của nghệ thuật Lý Bạch, Đỗ Phủ, Bạch Cư Dị.</p><p>Tiểu thuyết phát triển dưới thời Minh, Thanh, nhiều tác phẩm nổi tiếng như:</p><ul><li>Tam Quốc Diễn Nghĩa của La Quán Trung.</li><li>Thủy Hử của Thị Nại Am.</li><li>Tây Du Ký của Ngô Thừa Ân.</li><li>Hồng Lâu Mộng của Tào Tuyết Cần.</li></ul><img class=\"img-responsive\" src=\"public/images/noidung/bai6/Tu-dai-danh-tac-Trung-Hoa-World-History.jpg\"></img><blockquote>Hình 6.5: Tứ đại danh tác Trung Hoa</blockquote><p>Toán học: Cửu chương toán thuật (Hán) tính diện tích và khối lượng khác nhau.</p><p>Thiên văn học: Nông lịch phục vụ cho sản xuất; địa động nghi để đo động đất.</p><p>Y dược: đạt nhiều thành tựu quan trọng: thầy thuốc Hoa Đà (Hán) dùng phẫu thuật để chữa bệnh; sách thuốc Bản thảo cương mục của Lý Thời Trân.</p><p>Kỹ thuật: giấy, kỹ thuật in, la bàn, thuốc súng.</p><p>Kiến trúc đặc sắc: Vạn lý trường thành, Tử cấm thành, Tượng phật bằng ngọc thạch... còn được lưu giữ đến ngày nay.</p><img class=\"img-responsive\" src=\"public/images/noidung/bai6/Van-ly-truong-thanh-World-History.jpg\"></img><blockquote>Hình 6.6: Vạn lý trường thành - Một trong 7 kỳ quan của thế giới</blockquote></div>', 'public/images/noidung/bai6/Trung-Quoc-thoi-phong-kien-phan-II.jpg', 9, 'public/video/Phong-kien-Trung-Quoc-World-History.mp4', NULL, NULL),
(6, 5, 'Các quốc gia Ấn và văn hóa truyền thống Ấn Độ', 'Cac-quoc-gia-An-va-van-hoa-truyen-thong-An-Do', '<p>Lãnh thổ Ấn Độ như hình “tam giác ngược”, hai bên giáp biển, cạnh phía bắc nối với châu Á, nhưng lại ngăn cách bởi dãy núi cao nhất thế giới – dãy Hi-ma-lay-a, nên còn được gọi là Tiểu lục địa Nam Á. Ấn Độ còn là một lãnh thổ khá rộng lớn, hơn 3 triệu km2, gấp khoảng 10 lần Việt Nam và gần 15 lần nước Anh. Hai bờ biển lại có hai dãy núi Đông Gát và Tây Gát, ngăn cách với cao nguyên Đê-can. Núi cao, rừng rậm, rừng nguyên sinh đã khiến lãnh thổ Ấn Độ bị ngăn cách nhau đáng kể giữa Đông và Tây, Bắc và Nam. Chỉ có miền Bắc là bằng phẳng bởi lưu vực của hai con sông lớn....</p>', '<div class=\"post\"><p>Lãnh thổ Ấn Độ như hình “tam giác ngược”, hai bên giáp biển, cạnh phía bắc nối với châu Á, nhưng lại ngăn cách bởi dãy núi cao nhất thế giới – dãy Hi-ma-lay-a, nên còn được gọi là Tiểu lục địa Nam Á. Ấn Độ còn là một lãnh thổ khá rộng lớn, hơn 3 triệu km2, gấp khoảng 10 lần Việt Nam và gần 15 lần nước Anh. Hai bờ biển lại có hai dãy núi Đông Gát và Tây Gát, ngăn cách với cao nguyên Đê-can. Núi cao, rừng rậm, rừng nguyên sinh đã khiến lãnh thổ Ấn Độ bị ngăn cách nhau đáng kể giữa Đông và Tây, Bắc và Nam. Chỉ có miền Bắc là bằng phẳng bởi lưu vực của hai con sông lớn. Xưa kia, Ấn Độ gồm cả sông ở Tây Bắc là sông Ấn (Indus), nhờ nó mà có tên gọi quyết định (Hindustan) – nơi khởi nguồn của nền văn hoá Ấn Độ. Còn ở Đông Bắc bán đảo là lưu vực sông Hằng (Ganga) rộng lớn và màu mỡ, là quê hương, nơi sinh trưởng của nền văn hoá truyền thống, của nền văn minh Ấn Độ.</p><h2>1. Thời kỳ các quốc gia đầu tiên.</h2><p>Khoảng 1500 năm TCN lưu vực sông Hằng hình thành một số quốc gia nhỏ, mạnh nhất là nước Magađa ( khoảng 500 năm TCN ).</p><p>Thế kỷ thứ III TCN, Asôca thống nhất Ấn Độ , tạo điều kiện truyền bá đạo Phật, xây dựng cột Asôca.</p><img class=\"img-responsive\" src=\"public/images/noidung/bai7/Vua-Asoca-World-History.jpg\"></img><blockquote>Hình 7.1: Vua Asôca - Ấn Độ</blockquote><h2>2. Thời kỳ Vương triều Gúp-ta và sự phát triển của nền văn hoá truyền thống Ấn Độ.</h2><p>Đến đầu Công nguyên, miền Bắc Ấn Độ đã được thống nhất lại, Ấn Độ bước vào thời kỳ phát triển cao và rất đặc sắc - thời Vương triều Gúp-ta.</p><p>Vương triều này do vua Gúp-ta lập, có vai trò tổ chức kháng cự, không cho các tộc ở Trung Á xâm lấn từ phía tây - bắc, thống nhất miền Bắc Ấn Độ; làm chủ gần như toàn bộ miền Trung Ấn Độ.</p><p>Nét đặc sắc nổi bật của thời kỳ vương triều Gúp-ta (319 – 467) là sự định hình và phát triển của nền văn hoá truyền thống ở Ấn Độ.</p><p>Ở bắc Ấn Độ, Đạo Phật đã được hình thành và phát triển.</p><p>Cùng với sự truyền bá Phật giáo và lòng tôn sùng đối với Phật, người ta đã làm nhiều ngôi chùa hang (đục đẽo hang đá thành chùa).</p><img class=\"img-responsive\" src=\"public/images/noidung/bai7/Quang-canh-ben-ngoai-chua-hang-Ajanta-o-An-Do-World-History.jpg\"></img><blockquote>Hình 7.2: Quang cảnh bên ngoài chùa hang Ajanta ở Ấn Độ</blockquote><p>Ngoài ra ở Ấn Độ, đạo Hindu (hay Ấn Độ giáo) cũng ra đời và phát triển. Ấn Độ giáo thờ rất nhiều thần thánh, mà chủ yếu là 4 thần: Bộ ba Bra-ma (thần Sáng tạo), Si-va (thần Huỷ diệt), Vi-snu (thần Bảo hộ) và In-đra (thần Sấm sét) là những lực lượng siêu nhiên mà con người phải sợ hãi.</p><p>Chữ viết: hệ chữ Phạn (Sanskrit), được hoàn thiện từ thời A-sô-ca cả chữ viết và ngữ pháp. Ngôn ngữ và văn tự phát triển là điều kiện để chuyển tải, truyền bá văn học, văn hoá Ấn Độ.</p><img class=\"img-responsive\" src=\"public/images/noidung/bai7/Chu-Phan-World-History.jpg\"></img><blockquote>Hình 7.3: Chữ Phạn (Sankrit)</blockquote><p>Thời Gúp-ta đã có những công trình kiến trúc, tượng, những tác phẩm văn học tuyệt vời, làm nền tảng cho văn hoá truyền thống Ấn Độ.</p><p>Ảnh hưởng văn hóa Ấn Độ  ra bên ngoài: các nước Đông Nam Á là nơi chịu ảnh hưởng rõ rệt nhất của văn hóa Ấn Độ.</p><p>Yếu tố ảnh hưởng là tôn giáo, kiến trúc, chữ viết, tôn giáo (Việt Nam cũng ảnh hưởng của văn hoá Ấn Độ như tháp Chàm, đạo Phật, đạo Hinđu).</p><img class=\"img-responsive\" src=\"public/images/noidung/bai7/Ton-giao-o-An-Do-World-History.jpg\"></img><blockquote>Hình 7.4: Tôn giáo ở Ấn Độ</blockquote></div>', 'public/images/noidung/bai7/An-Do-co-World-History.jpg', 19, NULL, '2018-04-05 17:00:00', NULL),
(7, 5, 'Sự phát triển lịch sử và nền văn hóa đa dạng của Ấn Độ', 'Su-phat-trien-lich-su-va-nen-van-hoa-da-dang-cua-An-Do', '<p>Ấn Độ thời Gúp – ta bắt đầu phát triển trên lưu vực sông Hằng ở phía Bắc, làm cơ sở hình thành nền văn hóa truyền thống Ấn Độ - văn hóa Hinđu. Sau đó, nền văn hóa đó tiếp tục phát triển trong các thời kì sau cùng các nền văn hóa khác....</p>', '<div class=\"post\"><h2>1. Sự phát triển  của lịch sử và văn hóa  truyền thống trên toàn lãnh thổ.</h2><p>Thế kỷ VII Ấn Độ  lại phân tán.</p>\r\n<p>Hai nước  phát triển nhất là Pa-la (Đông Bắc) và Pa-la-va (miền Nam)</p><p>Nước Pa -la -va ở miền Nam  buôn bán đường biển với các nước Đông Nam Á  và Tây Á phát đạt nên phổ biến văn hóa Ấn Độ.</p><h2>2. Vương triều Hồi giáo Đê li. Từ 1206-1526.</h2><p>Thế kỷ XI – XII người Thổ Nhĩ Kỳ lập vương triều Hồi Giáo Đê li  đã  áp đặt  Hồi Giáo và cấm đạo Hin đu, chiếm đoạt ruộng đất;ra sức  bóc lột nhân dân Ấn, kỳ thị tôn giáo và giai cấp  gây mâu thuẫn dân tộc.</p><p>Phổ biến văn hóa Hồi giáo: Kiến trúc Hồi giáo.</p><p>Các thương nhân Ấn Độ truyền  bá đạo Hồi đến  một số  nước Đông nam Á.</p><h2>3.Vương triều Mô gôn (1526-1707):</h2><p>Vua Ti -mua Leng  tự nhận là dòng dõi Mông Cổ tấn công Ấn Độ  năm 1398, nhưng  cháu nội là Ba-bua  đánh chiếm Đê-li lập ra Vương triều Ấn Độ Mô-gôn (gốc Mông Cổ ).</p><p>Vua A-cơ-ba (1556-1707) tài giỏi  đã xây dựng một chính quyền mạnh mẽ, xây dựng khối hòa hợp  dân  tộc, khôi phục kinh tế, phát triển văn hóa Ấn Độ.</p><p>Vua A-cơ-ba được xem như một vị anh hùng dân tộc.</p><img class=\"img-responsive\" src=\"public/images/noidung/bai8/Vua-A-co-ba-World-History.jpg\"></img><blockquote>Hình 8.1: Vua A-cơ-ba Ấn Độ</blockquote><p>Nhưng đến thời Gia-han-ghi-a (1605-1627) và Sa Gia-han (1627-1658)  đã dùng  nhiều biện pháp quyết liệt để bắt dân phục tùng, chiếm đoạt nhiều của cải, xây dựng nhiều công trình kiến trúc: lăng mộ Ta–giơ Ma–han, lâu đài Thành Đỏ  ở Bắc Ấn Độ đó  là di sản văn hóa bất hủ.</p><p>Tình trạng chia rẽ  lại xuất hiện ở Ấn Độ.</p><img class=\"img-responsive\" src=\"public/images/noidung/bai8/Den-Taj-Ma-hal-An-Do-World-History.jpg\"></img><blockquote>Hình 8.2: Đền Taj-Ma-hal - Một trong 7 kỳ quan của thế giới</blockquote><img class=\"img-responsive\" src=\"public/images/noidung/bai8/Thanh-do-Laquila-An-Do-World-History.jpg\"></img><blockquote>Hình 8.3: Thành Đỏ Laquila</blockquote><p>Đầu thế kỷ XIX  thực dân phương Tây  xâm nhập Ấn Độ  báo hiệu sự khủng hoảng và suy vong của chế độ phong kiến An Độ.</p></div>', 'public/images/noidung/bai8/Kien-truc-An-Do-World-History.jpg', 5, NULL, '2018-04-05 17:00:00', NULL),
(8, 5, 'Sự hình thành và phát triển các quốc gia chính ở Đông Nam Á', 'Su-hinh-thanh-va-phat-trien-cac-quoc-gia-chinh-o-Dong-Nam-A', '<p>Đông Nam Á đã có người cư trú từ thời tiền sử. Các cộng đồng trong vùng đã tiến hóa để hình thành các nền văn hóa phức tạp hơn với những ảnh hưởng ở mức độ khác nhau từ Ấn Độ và Trung Quốc.</p><p>Các vương quốc cổ có thể được chia thành hai nhóm khác biệt. Nhóm thứ nhất là các vương quốc trồng trọt. Các vương quốc trồng trọt coi nông nghiệp làm hoạt động kinh tế chính. Đa số các quốc gia trồng trọt nằm ở vùng lục địa Đông Nam Á. Ví dụ như Văn Lang, nằm ở đồng bằng sông Hồng. Kiểu thứ hai là các quốc gia gần biển dựa vào hoạt động thương mại hàng hải như Phù Nam nằm ở hạ lưu đồng bằng sông Mekong.</p>', '<div class=\"post\"><h2>1. Sự ra đời của các vương quốc cổ ở Đông Nam Á</h2><img class=\"img-responsive\" src=\"public/images/noidung/bai9/Dong-nam-a-2-World-History.jpg\"></img><blockquote>Hình 9.1: Các nước Đông Nam Á</blockquote><p>Hiện nay Đông Nam Á có 11 nước là Việt nam, Lào, Campuchia,Thái Lan, Mianma, Ma lai xi a, Xingapo,In đô nê xi a, Phi lip pin,Bru nây, Đông Ti mo.</p><h4>Điều kiện tự nhiên: Địa hình rộng lớn, bị chia cắt bởi những dãy núi đá vôi, không có đồng bằng rộng lớn; khí hậu nhiệt đới gió mùa, thuận lợi kinh tế nông nghiệp trồng lúa nước và nhiều loại cây trồng khác.</h4><h4>Điều kiện ra đời các vương quốc cổ ở Đông Nam á.</h4><p>Thời đồ đá  Người  tối cổ  ở khắp Đông Nam Á.</p>\r\n<p>Thế kỷ  đầu  Công Nguyên biết dùng đồ sắt:  kinh tế nông nghiệp trồng lúa nước là chủ yếu, nghề thủ công truyền thống phát triển như dệt, làm gốm, đúc đồng và rèn sắt. Buôn bán đường biển rất phát triển, một số thành thị hải cảng ra đời, xuất hiện các trung tâm buôn bán nổi tiếng. Hải cảng Óc Eo (An giang),Ta-kô-la ( Mã Lai)v à bắt đầu xuất hiện các quốc gia  nhỏ đầu tiên.</p><p>10 thế kỷ đầu sau Công Nguyên xuất hiện các quốc gia nhỏ  như Champa, Phù Nam, các vương quốc hạ lưu sông Mê Nam,đảo In đô nê xi a.</p><p>Các quốc gia này tranh chấp lẫn nhau dẫn đến sự sụp đổ của  các vương quốc cổ, từ đó hình thành  các quốc gia phong kiến hùng mạnh sau này.</p><img class=\"img-responsive\" src=\"public/images/noidung/bai9/Vuong-quoc-cam-pu-chia-World-History.jpg\"></img><blockquote>Hình 9.2: Vương quốc Cam puchia  thế kỷ XII</blockquote><h2>2. Sự hình thành và phát triển của các quốc gia phong kiến Đông Nam Á</h2><h4>Từ thế kỷ VII đến X, tại Đông Nam Á đã hình thành một số quốc gia phong kiến dân tộc:</h4>\r\n<p>Như Vương quốc Cam puchia của người Khơ me.</p><p>Vương quốc của người Môn và người Miến  ở hạ lưu sông Mê nam.</p><p>Vương quốc của người In đô nê xi a ở Xu ma tra và Gia va….</p><h4>Từ thế kỷ X đền  XV III  hình thành, phát triển và thịnh đạt:</h4><p>Inđônêxia thống nhất và phát triển hùng mạnh dưới vương triều Mô-giô-pa-hít (1213 - 1527).</p><p>Bán đảo Đông Dương  có Đại Việt, Champa, Campuchia. Pagan (Mianma) ở lưu vực sông I-ra–oa-đi.</p><p>Người Thái ở thượng nguồn sông Mê Kông  di cư xuống phía  nam  lập ra Su-khô-thay (Thái lan)ở lưu vực sông Mê-nam ;và Lạn Xạng(Lào)ở trung lưu sông Mê-Công.</p><p>Đây cũng là giai đoạn kinh tế phát triển thịnh vượng, cùng với sự phát triển văn hóa riêng biệt.</p><h4>Sau thế kỷ XVIII  Đông Nam Á cổ suy yếu nhưng xã hội phong kiến vẫn tồn tại.</h4><h4>Giữa thế kỷ XIX bị phương Tây xâm chiếm</h4><img class=\"img-responsive\" src=\"public/images/noidung/bai9/Thuc-dan-phuong-Tay-xam-luot-DNA-World-History.jpg\"></img><blockquote>Hình 9.3: Thực dân phương Tây xâm lượt ĐNÁ</blockquote></div>', 'public/images/noidung/bai9/Dong-nam-a-World-History.jpg', 3, NULL, '2018-04-05 17:00:00', NULL),
(9, 5, 'Vương quốc Campuchia và vương quốc Lào', 'Vuong-quoc-Campuchia-va-vuong-quoc-Lao', '<p>Vương quốc Lào (tiếng Lào: ພຣະຣາຊອານາຈັກລາວ / Phra Ratxa A-na-chak Lao, tiếng Pháp: Royaume du Laos) là một chính thể tồn tại từ năm 1947 cho đến khi lực lượng Pathet Lào cưỡng bức giải thể để thay thế bằng Cộng hòa Dân chủ Nhân dân Lào vào tháng 12 năm 1975.</p><p>Vương quốc Campuchia, dưới thời Norodom Sihanouk từ năm 1953 đến năm 1970, là một giai đoạn đặc biệt quan trọng trong lịch sử đất nước. Sihanouk tiếp tục là một trong những nhân vật gây nhiều tranh cãi nhất trong lịch sử hậu thế chiến hỗn loạn và thường bi thảm của Đông Nam Á.</p>', '<div class=\"post\"><h2>1. Vương quốc Cam pu chia</h2><p>Là một trong những nước có lịch sử lâu đời và phát triển  nhất ở Đông nam Á.</p><p>Tộc người Khơ me (thuộc nhóm Môn c),sống ở phía bắc Cam-pu-chia, giỏi săn bắn, đào ao, đắp hồ trữ nước,  biết khắc chữ Phạn  ảnh hưởng văn hóa Ấn Độ.</p><p>Từ thế kỉ VI đến VIII lập nước Chân Lạp.</p><p>Thế kỷ IX  đến  XV  là thời kỳ phát triển của  vương quốc Cam pu chia (Ăng co huy hoàng):</p><ul><li>Nông nghiệp, lâm nghiệp và ngư nghiệp, thủ công nghiệp  phát triển.</li><li>Mở rộng lãnh thổ về phía đông: tiến đánh Cham pa, trung và  hạ lưu  sông Mê nam (Thái Lan), trung lưu sông Mê Công (Lào), tiến đến bắc bán đảo Mã Lai.</li><li>Kinh đô Ăng co với đền tháp đồ xộ như Angcovát, AngcoThom.</li></ul><p>Cuối thế kỷ XIII  suy yếu, sau 5 lần bị người Thái xâm, năm 1432 người Khơ me bỏ kinh đô Ang co, lui về phía cư trú nam Biển Hồ (Phnôm Pênh). Năm 1863  bị Pháp xâm lược.</p><img class=\"img-responsive\" src=\"public/images/noidung/bai10/Vuong-quoc-cam-pu-chia-World-History.jpg\"></img><blockquote>Hình 10.1: Lược đồ vương quốc Cam pu chia thế kỷ XII</blockquote><h4>Văn hóa: rất độc đáo</h4><p>Có chữ viết riêng  từ chữ Phạn.</p><p>Văn học dân gian  và văn học viết phản ảnh tình cảm của con người  đối với thiên nhiên, con người.</p><p>Kiến trúc Hin đu giáo và kiến trúc Phật giáo:quần thể Ang co Vát và Ang co Thom.</p><img class=\"img-responsive\" src=\"public/images/noidung/bai10/Chu-Kho-me-World-History.png\"></img><blockquote>Hình 10.2: Chữ Khơ-me</blockquote><img class=\"img-responsive\" src=\"public/images/noidung/bai10/Den-Ang-co-vat-World-History.jpg\"></img><blockquote>Hình 10.3: Ăng-co-vát - biểu tượng của vương quốc Cam-pu-chia</blockquote><img class=\"img-responsive\" src=\"public/images/noidung/bai10/Trang-phuc-truyen-thong-cua-nguoi-dan-Cam-pu-chia-World-History.jpg\"></img><blockquote>Hình 10.4: Trang phục truyền thống của người Cam-pu-chia</blockquote><h2>4. Vương quốc Lào</h2><p>Người Lào Thơng tạo ra chum đá (cánh đồng Chum ở Xiêng Khoảng).</p><p>Thế kỷ XIII người Thái di cư đến gọi là  người Lào Lùm</p><p>Năm 1353 Pha Ngừm  lập nước Lan Xang  - Triệu Voi</p><p>Vua Lan-Xang (XV – XVII): chia đất nước thành các mường, xây dựng quân đội, quan hệ hòa hiếu với các nước láng giềng. Cương quyết chống xâm lược Miến Điện.</p><p>Thế kỷ XVIII Lang Xang suy yếu bị Xiêm chiếm (khởi nghĩa của Chậu A Nụ  chống Xiêm năm 1827).</p><p>Thế kỷ XIX là thuộc địa của Pháp.</p><p>Người Lào thích ca hát.</p><p>Thế kỷ XIII, đạo phật truyền vào theo một dòng mới.</p><img class=\"img-responsive\" src=\"public/images/noidung/bai10/That-Luong-World-History.jpg\"></img><blockquote>Hình 10.5: Thát Luồng - biểu tượng của đất nước Lào</blockquote><img class=\"img-responsive\" src=\"public/images/noidung/bai10/Canh-dong-Chum-World-History.jpg\"></img><blockquote>Hình 10.6: Cánh đồng Chum - Lào</blockquote><img class=\"img-responsive\" src=\"public/images/noidung/bai10/Trang-phuc-truyen-thong-cua-nguoi-dan-Lao-Thong-World-History.jpg\"></img><blockquote>Hình 10.7: Trang phục truyền thống của người Lào</blockquote>\r\n</div>', 'public/images/noidung/bai10/Vuong-quoc-Lao-va-cam-pu-chia-World-History.jpg', 2, NULL, '2018-04-05 17:00:00', NULL),
(10, 3, 'Các quốc gia cổ đại phương Tây', 'Cac-quoc-gia-co-dai-phuong-Tay', '<p>Vào thời kỳ cổ đại, nền văn minh Cổ Hy Lạp nở rộ ở châu Âu, mở đầu với hai nền văn minh Minos và Mycenae, và phát triển hoàng kim từ thế kỷ thứ 8 trước Công Nguyên, với chiến thắng của nhân dân Hy Lạp trước các cuộc xâm lược của Đế quốc Ba Tư, trong thời này thị quốc Athena giàu mạnh đã có nền dân chủ, cho đến khi một nước Hy hóa lân cận là Macedonia làm bá chủ Hy Lạp. Với vua Alexandros Đại Đế, người Macedonia đã mở mang nền văn minh Hy Lạp đến tận Á châu, mở ra thời kỳ Hy Lạp hóa. Sau đó, vào năm 31 trước Công Nguyên, Hoàng đế Augustus sau khi chiến thắng Vương triều Hy hóa của Ai Cập, đã lập nên Đế quốc La Mã.</p>', '<div class=\"post\"><h2>1.Thiên nhiên và đời sống của con người</h2><p>Hy Lạp, Rôma nằm ở ven biển Địa Trung Hải, nhiều đảo, đất canh tác ít và khô cứng, đã tạo ra những thuận lợi và khó khăn:</p><ul><li>Thuận lợi: có biển, hải cảng, khí hậu ấm áp, nên giao thông thuận lợi, sớm phát triển nghề hàng hải, ngư nghiệp, thương nghiệp biển.</li><li>Khó khăn: đất xấu, ít, thích hợp trồng cây lưu niên như nho, ô liu, cam chanh. Lương thực thiếu phải mua lúa mì, lúa mạch của người Ai Cập, Tây Á.</li></ul><p>Khoảng đầu thiên nhiên kỷ I TCN, cư dân Địa Trung Hải bắt đầu biết chế tạo công cụ bằng sắt, cho phép khai hoang trên diện tích rộng hơn.</p><img class=\"img-responsive\" src=\"public/images/noidung/bai4/Xa-hoi-Ro-ma-co-dai-World-History.jpg\"/></img><blockquote>Hình 4.1: Xã hội Rô-ma cổ đại</blockquote><p>Thủ công nghiệp rất phát đạt, có nhiều ngành nghề, nhiều thợ giỏi, hàng hóa đẹp, chất lượng cao, quy mô lớn.</p><p>Thương nghiệp đường biển rất phát triển, mở rộng buôn bán với mọi ven Địa Trung Hải, với các nước phương Đông.</p><p>=>Như vậy cuộc sống ban đầu của cư dân Địa Trung Hải: sớm biết buôn bán, đi biển và trồng trọt (kinh tế mở).</p><h2>2.Thị quốc Địa Trung Hải (Thành bang)</h2><p>Nguồn gốc hình thành: do tình trạng đất đai phân tán nhỏ và cư dân sống bằng nghề thủ công và thương nghiệp nên đã hình thành các thị quốc.</p><p>Tổ chức của thị quốc: nước thì nhỏ, nghề buôn phát triển nên cư dân tập trung ở thành thị có phố sá, lâu đài đền thờ, sân vận động, nhà hát và bến cảng, ta gọi đó là thị quốc. A-ten là thị quốc, đại diện cho cả At-tích.</p><p>Tính chất dân chủ của thị quốc: quyền lực không nằm trong tay quý tộ mà nằm trong tay Đại hội công dân. Đại hội công dân bầu và cử ra các cơ quan nhà nước, (không có vua), quyết định mọi công việc nhà nước. Hội đồng 500 có vai trò như Quốc hội, người ta bầu 10 viên chức điều hành chính phủ, nhiệm kỳ một năm.</p><p>Bản chất của nền dân chủ cổ đại ở Hy Lạp, Rô-ma: đó là nền dân chủ chủ nô, dự vào sự bóc lột thậm tệ của chủ nô đối với nô lệ.</p><p>Hi Lạp giàu có nhờ nền kinh tế công thương nghiệp, sử dụng lao động nô lệ. Nô lệ bị bóc lột nên phản kháng chủ nô.</p><h2>3.Văn hóa cổ đại Hi Lạp và Rô-ma</h2><h4>a.Lịch và chữ viết</h4><h5>Lịch<h5><p>Người Hy Lạp có hiểu biết chính xác hơn về trái đất và hệ mặt trời, người Rô-ma tính một năm 365 ngày và 1/4, định ra một tháng lần lượt có 30 và 31 ngày, riêng tháng hai có 28 ngày, rất gần bộ lịch được sử dụng phổ biến ngày nay.</p><h5>Chữ viết</h5><p>Sáng tạo ra chữ viết gồm ký hiệu đơn giản, có khả năng ghép chữ rất linh hoạt thành từ để thể hiện ý nghĩa của con người.</p><p>Hệ thống chữ cái Rô-ma A, B, C... ra đời (hệ chữ cái Latinh), ban đầu gồm 20 chữ, sau thêm 6 chữ. Đây là một phát minh và cống hiến lớn lao cho nhân loại.</p><img class=\"img-responsive\" src=\"public/images/noidung/bai4/Chu-cai-La-tinh-o-cac-quoc-gia-co-dai-phuong-tay-World-History.jpg\"/></img><blockquote>Hình 4.2: Chữ cái La-tinh ở các quốc gia cổ đại phương Tây</blockquote><h4>b.Sự ra đời của khoa học</h4><p>Đến thời cổ đại Hy Lạp - Rô-ma, những hiều biết khoa học mới thực sự trở thành khoa học vì có độ chính xác của khoa học đạt tới trình độ khái quát định lý, lý thuyết.</p><p>Toán học: định lý Ta-lét, tiên đề Ơ-clit, định lý Pi-ta-go.</p><img class=\"img-responsive\" src=\"public/images/noidung/bai4/Dinh-ly-Ta-let-World-History.jpg\"/></img><blockquote>Hình 4.3: Định lý Ta-lét</blockquote><img class=\"img-responsive\" src=\"public/images/noidung/bai4/Pitago-World-History.jpg\"/></img><blockquote>Hình 4.4: Nhà toán học Pitago</blockquote><h4>c.Văn học:</h4><p>Ở Hy Lạp, sau bản anh hùng ca nổi tiếng của Hô-me là I-li-át và Ô-đi-xê, đã xuất hiện những nhà viết kịch nổi tiếng.</p><p>Người Rô-ma tự nhận là học trò và người thừa kế của văn học - nghệ thuật Hy Lạp và ở đây cũng đã xuất hiện nhà thơ nổi tiếng như Lu-cre-xơ, Viếc-gin,...</p><img class=\"img-responsive\" src=\"public/images/noidung/bai4/Nha-van-Homer-World-History.jpg\"></img><blockquote>Hình 4.5: Nhà văn Homer - Hy Lạp cổ</blockquote><h4>d.Nghệ thuật</h4><p>Hy Lạp để lại nhiều tượng và đền đài đạt trình độ tuyệt mỹ, khiến đời sau khâm phục: tượng lực sĩ ném đĩa, tượng nữ thần A-thê-na đội mũ chiến binh, thần vệ nữ Mi-lô,...</p><p>Kiến trúc: đền Pác-tê-nông ở A-ten (Hy Lạp), đấu trường Cô-li-dê ở Rô-ma,...</p><img class=\"img-responsive\" src=\"public/images/noidung/bai4/Tuong-ve-nu-Mi-lo-World-History.jpg\"/></img><blockquote>Hình 4.6: Tượng vệ nữ Mi-lô</blockquote><img class=\"img-responsive\" src=\"public/images/noidung/bai4/Den-pac-te-nong-World-History.jpg\"/></img><blockquote>Hình 4.7: Đền Pa-tê-nông</blockquote></div>', 'public/images/noidung/bai4/Cac-quoc-gia-co-dai-phuong-Tay-World-History.png', 2, NULL, '2018-04-04 17:00:00', NULL),
(11, 6, 'Thời kỳ hình thành và phát triển của chế độ phong kiến ở Tây Âu', 'Thoi-ky-hinh-thanh-va-phat-trien-cua-che-do-phong-kien-o-Tay-Au', '<p>Giai đoạn Trung Cổ được đánh dấu bằng sự tái thiết xã hội có tổ chức, chủ yếu là theo chế độ phong kiến, và sự thống trị ở phương Bắc của Giáo hội Công giáo Rôma. Ở phía đông, Đế quốc Đông La Mã hưng thịnh, với các Hoàng đế tài ba như Heraclius, là một chiến tướng kiệt xuất đánh bại quân Ba Tư vào thập niên 620. Song, chính ngay từ thời điểm này người Ả Rập Hồi giáo càn quét châu Âu và không ít khi đánh thắng Heraclius, dù rằng người Đông La Mã vẫn mạnh lên dưới triều Hoàng đế Basil II, và trong trận Tours (732), Vương quốc Frank (người German) đã đánh tan tác quân Ả Rập. Vào năm 800, sau khi đã bành trướng nước Frank cường thịnh, chinh phạt các tộc German khác, vua Karl Đại đế được Giáo hoàng Lêô III phong làm Hoàng đế Công giáo ở phương Tây đối trọng với Đông La Mã....</p>', '<div class=\"post\"><h2>1.Sự hình thành các vương quốc phong kiến Tây Âu</h2><img class=\"img-responsive\" src=\"public/images/noidung/bai11/Cac-quoc-gia-phong-kien-Tay-Au-World-History.jpg\"></img><blockquote>Hình 11.1: Lược đồ các quốc gia phong kiến Tây Âu</blockquote><p>Từ thế kỷ III, đế quốc Rô ma lâm vào tình trạng khủng hoảng suy vong, giữa lúc ấy người Giéc man  từ phương Nam tràn xuống xâm chiếm.</p><p>Năm 476, đế quốc Rô ma bị diệt vong, chế độ chiếm hữu nô lệ kết thúc, thời đại phong kiến bắt đầu ở châu Âu.</p><p>Khi vào lãnh thổ của Rô ma, người Giéc-man đã:</p><ul><li>Thủ tiêu bộ máy nhà nước Rô ma, lập nhiều vương quốc mới  như vương quốc Ang lô- Xắc xông, Phơ răng, Tây Gốt, Đông Gốt.</li><li>Chủ  đất  của chủ nô cũ  được chia cho quý tộc  và tướng lĩnh quân sự.</li><li>Tự phong các tước vị, hình thành tầng lớp quý tộc.</li><li>Ki tô giáo  dần dần  có vai trò và có ưu thế  trong đời sống nhân dân.</li><li>Tầng lớp quý tộc và tăng lữ  được hình thành  có đặc quyền và giàu có, trở thành các lãnh chúa phong kiến, còn nô lệ  và nông dân  biến thành nông nô  phụ thuộc lãnh chúa.Quan hệ sản xuất  phong kiến Châu Âu hình thành.</li></ul><img class=\"img-responsive\" src=\"public/images/noidung/bai11/Su-sup-do-cua-de-che-La-ma-World-History.jpg\"></img><blockquote>Hình 11.2: Sự sụp đổ của đế chế La mã</blockquote><img class=\"img-responsive\" src=\"public/images/noidung/bai11/Mo-hinh-lanh-dia-phong-kien-World-History.jpg\"></img><blockquote>Hình 11.3: Mô hình một lãnh địa phong kiến</blockquote><img class=\"img-responsive\" src=\"public/images/noidung/bai11/Lau-dai-lanh-chua-World-History.jpg\"></img><blockquote>Hình 11.4: Lâu đài của lãnh chúa</blockquote><h2>2.Xã hội phong kiến Tây Âu</h2><h4>a. Sự hình thành</h4><p>Đến giữa thế kỷ IX, phần lớn đất đai đã được quý tộc và nhà thờ chia nhau chiếm đoạt xong  gọi là lãnh địa phong kiến, đây là thời kỳ phân quyền.</p><p>Chủ của lãnh địa gọi là lãnh chúa.</p><p>Lãnh địa gồm đất của lãnh chúa và đất khẩu phần.</p><p>Người sản xuất chính là nông nô, nô lệ phụ thuộc vào lãnh chúa, phải nộp tô phục dịch, cung đốn cho lãnh chúa, bị bóc lột họ đã vùng lên đấu tranh.</p><h4>b. Sự phát triển và đặc điểm kinh tế</h4><p>Kỹ thuật canh tác tiến bộ.</p><p>Quan hệ sản xuất phong kiến: lãnh chúa bóc lột nông nô.</p><p>Kinh tế tự cung tự cấp.</p><p>Mỗi lãnh địa là một đơn vị độc lập, chế độ phong kiến phân quyền.</p><p>Các lãnh chúa sống nhàn rỗi, xa hoa, họ bóc lột tô thuế  và sức lao động của nông nô. Nông nô nổi dậy đấu tranh như khởi nghĩa Giắc cơ ri ở Pháp năm 1358...</p><h2>3.Sự xuất hiện thành thị trung đại</h2><p>Do sản xuất phát triển từ thế  kỷ XI, nên xuất hiện tiền đề nền kinh tế hàng hóa.</p><p>Năng suất lao động tăng  tạo ra nhiều sản phẩm thừa.</p><p>Thủ công nghiệp tách khỏi nông nghiệp, và tách khỏi lãnh địa, hàng hóa bán ra thị trường một cách tự do, thường tập trung ở nơi đông người, các ngã ba đường, bến sông để buôn bán trao đổi, lập ra thị trấn, sao trở thành thành thị.</p><p>Trong thành thị có các thương hội và phường hội.</p><p>Sự phát triển của thủ công nghiệp và thương nghiệp trong thành thị đã phá vỡ nền kinh tế tự nhiên của các lãnh địa, tạo điều kiện cho kinh tế hàng hóa phát triển.</p><p>Thành thị đã xóa bỏ chế độ phong kiến phân quyền, xây dựng chế độ phong kiến tập quyền, thống  nhất quốc gia dân tộc.</p><p>Mang không khí tự do, mở mang trí thức, các trường đại học ra đời như Bô lô nha (Ý). O-xphớt (Anh), Xooc–bon (Pháp).</p><img class=\"img-responsive\" src=\"public/images/noidung/bai11/Hoi-cho-o-Duc-World-History.jpg\"></img><blockquote>Hình 11.5: Hội chợ ở Đức.</blockquote><img class=\"img-responsive\" src=\"public/images/noidung/bai11/Canh-sinh-hoat-trong-thanh-thi-phuong-Tay-World-History.jpg\"></img><blockquote>Hình 11.6: Cảnh sinh hoạt trong thành thị phương Tây.</blockquote></div>', 'public/images/noidung/bai11/Chau-Au-thoi-ky-phong-kien-World-History.jpg', 2, NULL, '2018-04-05 17:00:00', NULL);
INSERT INTO `sukien` (`MaSK`, `MaCTGD`, `TieuDe`, `TieuDeKhongDau`, `TomTatSK`, `NoiDung`, `HinhSK`, `SoLuotXem`, `Video`, `created_at`, `updated_at`) VALUES
(12, 4, 'Văn hóa cổ đại', 'Van-hoa-co-dai', '<p>Trả lời một số câu hỏi về văn hóa thời cổ đại</p>', '<div class=\"post\"><h2>1.Các dân tộc phương Đông thời cổ đại đã có những thành tựu văn hóa gì?</h2><p>Thiên văn, lịch (âm lịch), làm đồng hồ.</p>\r\n<p>Chữ tượng hình Ai Cập, Trung Quốc, được viết trên giấy Pa pi rút, trên mai rùa, thẻ tre, đất sét…</p><p>Giấy Pa-pi-rút</p><p>Toán học:</p><ul><li>Người Ai Cập nghĩ ra phép đếm đến 10, giỏi về hình học, tìm được số pi bằng 3.16</li><li>Người Lưỡng Hà giỏi về số học</li><li>Người An Độ tìm ra các chữ số (kể cả số 0)</li></ul><p>Kiến trúc: Kim tự  tháp Ai Cập, thành Ba- bi- lon (Lưỡng Hà),...</p><h2>2. Người Hy Lạp và Rô- ma có những đóng góp gì về văn hóa?</h2><p>Thiên văn, lịch (dương lịch)</p><p>Chữ viết: sáng tạo Hệ chữ cái a, b, c,...</p><p>Chữ cái cổ Hy Lạp và La tinh.</p><p>Chữ số: Số thường 1, 2, 3,... và số La Mã I, II, III,...</p><p>Các ngành khoa học cơ bản đạt nhiều thành tựu rực rỡ:  Toán học, Thiên văn, Vật\r\nlý,  Sử học, Triết học,...</p><p>Văn học cổ Hy Lạp phát triển rực rỡ với những vở kịch thơ độc đáo: Ê- đíp làm vua,...</p><p>Kiến trúc: Đền Pác Tê- nông (Aten), đấu trường Cô- li- dê (Rô- ma), tượng lực sĩ ném đĩa, Thần Vệ Nữ,...</p><h2>HƯỚNG DẪN TRẢ LỜI CÂU HỎI</h2><h3>1.Vì sao các dân tộc phương Đông cổ đại sớm làm ra lịch?</h3><p>Trả lời:</p><p>Để phục vụ yêu cầu sản xuất nông nghiệp, cày cấy cho đúng thời vụ, người phương Đông phải thường xuyên theo dõi bầu trời, trăng, sao, mặt trời....để tính toán cho đúng thời gian, gieo trồng cho hợp thời vụ => Từ đó lịch ra đời.</p><h3>2.Người nông dân dựa vào đâu để làm ra lịch?</h3><p>Trả lời:</p><p>Để cày cấy cho đúng thời vụ, những người nông dân phải :\" Trông trời, trông đất\".\r\nDần dần họ biết được sự chuyển động của Mặt Trời và các hành tinh khác ảnh hưởng tới việc \"mưa thuận gió hòa\" hàng năm. Từ đó họ sáng tạo ra lịch (Lịch người phương Đông chủ yếu là âm lịch)</p><h3>3.Trình bày những thành tựu tiêu biểu của văn hóa cổ đại phương Đông?</h3><p>Trả lời:</p><p>Biết làm ra lịch và dùng âm lịch : 1 năm có 12 tháng, mỗi tháng có 29 hoặc 30 ngày, biết làm đồng hồ đo thời thời gian bằng bóng nắng mặt trời.</p><p>Sáng tạo chữ viết, gọi là chữ tượng hình (vẽ mô phỏng vật thật để nói lên ý nghĩ của con người); viết lên giấy Pa-pi-rút, trên mai rùa, trên thẻ tre, trên các phiến đất sét...</p><p>Toán học : phát minh ra phép đếm đến 10, các chữ số từ 1 đến 9 và số 0, tính được số Pi bằng 3.16</p><p>Kiến trúc : các công trình kiến trúc đồ sộ như Kim tự tháp ở Ai Cập, thành Ba-bi-lon ở Lưỡng Hà.</p><h3>4.Thành tựu trong lĩnh vực toán học là của dân tộc nào?</h3><p>Trả lời:</p><p>Trong lĩnh vực toán học, người Ai Cập cổ đại đã nghĩ ra phép đếm đến 10 và rất giỏi về hình học, họ đã tính được số Pi bằng 3.1416</p><p>Người Lưỡng Hà giỏi về số học.</p><p>Người Ấn Độ phát minh ra các chữ số, kể cả số 0.</p><h3>5.Dân tộc nào ở phương Đông xuất hiễn chữ viết sớm? Vào khoảng thời gian nào? Họ viết chữ vào đâu?</h3><p>Trả lời:</p><p>Các dân tộc phương Đông xuất hiện chữ viết sớm đó là : Lưỡng Hà, Ai Cập, Trung Quốc.</p><p>Vào khoảng thời gian:</p><ul><li>Lưỡng Hà, Ai Cập: 3500 năm TCN</li><li>Trung Quốc: 2000 năm TCN</li></ul><p>Họ viết chữ vào:</p><ul><li>Người Ai Cập viết trên giấy làm từ vỏ cây Pa-pi-rút (một loại cây sậy)</li><li>Người Lưỡng Hà viết trên phiến đất sét ướt rồi đem nung khô.</li><li>Người Trung Quốc viết trên mai rùa, thẻ tre, trên mảng lụa trắng.</li></ul></div>', 'public/images/noidung/bai14/Van-hoa-co-dai-World-History.jpg', 1, NULL, '2018-04-05 17:00:00', NULL),
(13, 6, 'Tây Âu thời kỳ trung đại', 'Tay-Au-thoi-ky-trung-dai', '<p>Thời kỳ cuối Thời kỳ Trung Cổ kéo dài trong thế kỷ 14 và 15. Khoảng năm 1300, những thế kỷ thịnh vượng và phát triển của châu Âu tạm ngừng. Một loạt nạn đói và bệnh dịch, như Nạn đói Lớn 1315–1317 và Tử thần Đen, theo một số ước tính đã làm giảm một nửa dân số. Cùng với nạn giảm dân số là sự bất ổn xã hội và chiến tranh địa phương. Pháp và Anh đều phải đối đầu với những cuộc nổi dậy nông dân nghiêm trọng: Jacquerie, Nổi dậy Nông dân, và cuộc Chiến tranh một trăm năm. Càng làm gia tăng các vấn đề của giai đoạn này, sự thống nhất của Giáo hội Công giáo bị ảnh hưởng bởi cuộc Ly giáo Tây phương. Những sự kiện này thỉnh thoảng được gọi là Khủng hoảng cuối Thời Trung Cổ.</p>', '<div class=\"post\"><img class=\"img-responsive\" src=\"public/images/noidung/bai12/Luoc-do-phat-kien-dia-li-World-History.jpg\"></img><blockquote>Hình 12.1: Lược đồ phát khiến địa lí</blockquote><h2>1.Những cuộc phát kiến địa lí</h2><p>Nguyên nhân:</p><ul><li>Sản xuất phát triển, nhu cầu cần nguyên liệu, thị trường, vàng bạc tăng lên.</li><li>Con đường bộ  buôn bán qua Tây Á  và Địa Trung Hải   do người Ả rập độc chiếm.</li><li>Khoa học- kỹ thuật phát triển (Hải đồ, la  bàn, tàu có bánh lái  và hệ thống buồm lớn).</li></ul><img class=\"img-responsive\" src=\"public/images/noidung/bai12/Tau-Caraven-World-History.jpg\"></img><blockquote>Hình 12.2: Tàu Caraven</blockquote><p>Loại tàu do người Bồ Đào Nha chế tạo năm 1460, có bánh lái, lắp 3 cột buồm lớn. Cánh buồm của tàu hình vuông hoặc tam giác màu trắng. Trên boong tàu có lắp đại bác để chống cướp biển. Đuôi tàu trang bị 1 trục giữ bánh lái, có thể quay quanh bản lề, thay cho bánh lái mái chèo cổ xưa từ thế kỉ XII. Trên tàu có la bàn định hướng, đồng hồ cát bằng thủy tinh để đo thời gian và ước lượng kinh độ => có khả năng vượt đại dương.</p><h4>Những cuộc phát kiến địa lý lớn</h4><ul><li>B. Đi-a-xơ (1487): vòng qua cực nam Châu Phi đến mũi Hảo Vọng.</li><li>Cô-lôm-bô (1492) đến một số đảo  biển Ca ri bê đã  phát hiện ra Châu Mỹ.</li><li>Va-x-cô-đơ Gama (1497) đến bờ Tây nam Ấn Độ.</li><li>Ma-gien-lan (1519-1522) vòng quanh thế giới.</li></ul><img class=\"img-responsive\" src=\"public/images/noidung/bai12/Hai-trinh-cua-Di-a-xo-va-Va-x-co-do-Gama-World-History.jpg\"></img><blockquote>Hình 12.3: Hải trình của Đi-a-xơ và Va-x-cô-đơ Gama</blockquote><img class=\"img-responsive\" src=\"public/images/noidung/bai12/Magellan-World-History.jpg\"></img><blockquote>Hình 12.3: PERNANDO MAGELLAN (1480 - 1521)\r\n</blockquote><p>Nhà thám hiểm người Bồ Đào Nha dẫn đầu chuyến hành trình vòng quanh thế giới đầu tiên bằng đường biển cho Tây Ban Nha. Ông phát hiện ra eo biển Magellan. Ông bị giết chết ở Philippines năm 1521 và chuyến hành trình của được Sebastian del Cano (hoặc Elcano).</p><img class=\"img-responsive\" src=\"public/images/noidung/bai12/Hai-trinh-cua-Magellan-World-History.jpg\"></img><blockquote>Hình 12.3: Hải trình của PERNANDO MAGELLAN</blockquote><h2>2.Sự nảy sinh chủ nghĩa tư bản Châu Âu</h2><p>Sau các cuộc phát kiến địa lý, quý tộc và thương nhân Châu Âu  ra sức cướp bóc của cải, tài nguyên các nước Á, Phi, Mỹ La Tinh.</p><p>Giai cấp tư sản tích lũy  được số vốn đầu tiên  bằng sự cướp bóc thực dân,cướp đất của nông dân, nông dân phải làm thuê  cho giai cấp tư sản.</p><p>Xuất hiện hình thức kinh doanh tư bản chủ nghĩa: công trường thủ công, các công ty thương mại.</p><p>Công trường thủ công, trong đó  có sự phân công lao động, chuyên môn hóa theo dây chuyền sản xuất, quan hệ chủ thợ. Chủ kiếm nhiều lợi nhuận, thợ bị bóc lột.quan hệ sản xuất TBCN hình thành.</p><p>Ở nông thôn đồn điền hay trang trại xuất hiện, công nhân nông nghiệp làm công ăn lương.</p><p>Thương nghiệp  xuất hiện công ty thương mại.</p><p>Xã hội Tây Âu có biến đổi:</p><ul><li>Giai cấp tư sản có thế lực kinh tế song chưa có địa vị kinh tế tương xứng.</li><li>Người làm thuê trở thàng giai cấp vô sản.</li></ul><h2>3.Phong trào Văn hóa Phục hưng</h2><p>Phục hưng tinh hoa của nền  văn hóa cổ Hi Lạp – Rô ma và sáng tạo nền văn hóa mới của giai cấp tư sản.</p><h4>Đặc điểm:</h4><ul><li>Phê phán giáo hội phong kiến và giáo hội. Đề cao giá trị con người, đòi quyền tự do cá nhân, coi trọng khoa học - kỹ thuật.</li><li>Quê hương phong trào Văn hóa Phục Hưng là I–ta-li-a và lan nhanh sang các nước Tây Âu:<ul><li>Ra-bơ-e là nhà văn và Bác sĩ.</li><li>Đê-các-tơ là nhà toán học và triết học.</li><li>Lê-ô-na đơ Vanh-xi là họa sĩ, kỹ sư.</li><li>Sếch–xpia là nhà soạn kịch.</li></ul></li></ul><p>Đây là cuộc đấu tranh công khai đầu tiên trên lãnh vực văn hóa tư tưởng của giai cấp tư sản chống giai cấp phong kiến đã suy tàn.</p><h2>4. Cải cách tôn giáo và chiến tranh nông dân</h2><h4>a.Cải cách tôn giáo</h4><p>Nguyên nhân<p><ul><li>Ky tô giáo là chỗ dựa vững  chắc  của chế độ phong kiến Tây Âu, thống trị và chi phối  đời sống tinh thần  của xã hội Châu Âu.</li><li>Nước Đức là nơi đầu tiên nổ ra phong trào cải cách tôn giáo: Lu thơ (1483-1546) tại Đức ; Can-vanh (1509-1564) tại Thụy Sĩ , sau đó lan nhanh sang Bỉ, Hà Lan, Pháp, Anh.</li></ul><p>Nội dung cải cách: bãi bỏ thủ tục và lễ nghi phiền toái, được đông đảo nhân dân đi theo.</p><p>Tác dụng: thúc đẩy và châm ngòi cho các cuộc khởi nghĩa nông dân, tôn giáo bị phân hóa thành Tân giáo và Cựu giáo.</p><p>Đây là cuộc đấu tranh công khai đầu tiên trên lãnh vực văn hóa tư tưởng của giai cấp tư sản chống giai cấp phong kiến đã suy tàn.</p><h4>b. Chiến tranh nông dân Đức</h4><p>Nguyên nhân</p><ul><li>Kinh tế thấp kém, chế độ phong kiến bảo thủ.</li><li>Người nông dân bị áp bức bóc lột nặng nề.</li><li>Mâu thuẫn giữa nông dân và  quí tộc phát  triển đến đỉnh cao.</li></ul><p>Diễn biến:</p><ul><li>Lãnh tụ kiệt xuất là Tô -mát Muyn-xe: lên án gay gắt sự hủ bại của giáo hội, lên án chế độ bóc lột phong kiến, kêu gọi nông dân nổi dậy chống áp bức, tuyên truyền và xây dựng một xã hội bình đẳng cho mọi người.</li><li>Bước đầu giành thắng lợi, nhưng cuối cùng  bị đàn áp  nên thất bại.</li></ul><p>Ý nghĩa: thể hiện tinh thần đấu tranh quyết liệt và khí phách anh hùng của quần chúng bị áp bức.</p><p>Tô mat Muyn-xe  xuất thân từ 1 gia đình thợ mỏ ở XtonbecThuở nhỏ rất chăm học, 15 tuổi đã lập trong trường ông học 1 hội kín chống Giám mục  Ma đơ bua và nhà thờ La Mã.Sau khi đỗ tiến sĩ, ông trở thành Linh mục.Năm 1521, ông ra nước ngoài, sau đó trở về Đức vận động cách mạng. Ông rất đồng cảm với nhân dân, lên án gay gắt sự hủ bại của Giáo hội, lên án chế độ bóc lột phong kiến.Ông kêu gọi nông dân nổi dậy chống áp bức, tuyên truyền và mở cuộc xây dựng  một xã hội bình đẳng cho mọi người.</p></div>', 'public/images/noidung/bai12/Kham-pha-nhung-vung-dat-moi-World-History.jpg', 3, NULL, '2018-04-05 17:00:00', NULL),
(14, 7, 'Những nét chung về xã hội phong kiến', 'Nhung-net-chung-ve-xa-hoi-phong-kien', '<p>Trả lời một số câu hỏi về thời kỳ phong kiến cả ở các nước phương Tây và các nước phương Đông</p>', '<div class=\"post\"><h2>1.Sự hình thành và phát triển xã hội phong kiến</h2><p>Xã hội phong kiến phương Đông hình thành sớm, nhưng lại phát triển chậm chạp, quá trình khủng hỏang suy vong kéo dài.</p><p>Xã hội phong kiến Châu Âu được hình thành muộn hơn, kết thúc sớm hơn nhường chỗ cho chủ nghĩa tư bản.</p><h2>2. Cơ sở kinh tế - xã hội của xã hội phong kiến</h2><h4>Cơ sở kinh tế: chủ yếu là kinh tế nông nghiệp</h4><p>Ở phương Đông sản xuất nông nghiệp đóng kín trong các công xã nông thôn.</p><p>Châu Âu : đóng kín trong các lãnh địa phong kiến</p><h4>Xã hội phong kiến có 2 giai cấp cơ bản:</h4><p>Phương Đông : địa chủ và nông dân lĩnh canh.</p><p>Châu Âu là lãnh chúa và nông nô.</p><p>Bóc lột bằng tô thuế: Tuy nhiên ở Châu Âu sau khi thành thị trung đại, xuất hiện kinh tế công thương nghiệp phát triển và thị dân ra đời.</p><h2>3. Nhà nước phong kiến</h2><p>Chế độ quân chủ nhưng khác nhau về mức độ và thời gian.</p><p>Thể chế nhà nước do vua đứng đầu.</p><p>Ở phương Đông vua chuyên chế tăng thêm quyền lực - tập quyền ngay từ đầu.</p><p>Phương Tây từ phân quyền đến tập quyền.</p><h2>HƯỚNG DẪN TRẢ LỜI CÂU HỎI</h2><h3>1.Cơ sở kinh tế của xã hội phong kiến ở phương Đông và châu Âu có điểm gì giống và khác nhau?</h3><p>Trả lời:</p><p>Giống nhau: Nền kinh tế chủ yếu là nông nghiệp</p><p>Khác nhau:</p><ul><li>Phương Đông bó hẹp, đóng kín trong các công xã nông thôn</li><li>Phương Tây đóng kín trong lãnh địa phong kiến.</li></ul><h3>2.Xã hội phong kiến ở phương Đông và phương Tây có những giai cấp cơ bản nào?</h3><p>Trả lời:</p><p>Xã hội phong kiến phương Đông và phương Tây có những giai cấp cơ bản là:</p>\r\n<ul><li>Ở phương Đông: Địa chủ và nông dân</li><li>Ở phương Tây : Lãnh chúa và nông nô</li></ul><h3>3.Quan hệ giữa các giai cấp như thế nào?</h3>\r\n<p>Trả lời:</p><p>Địa chủ ở phương Đông và lãnh chúa ở châu Âu đều nắm ruộng đất trong tay giao cho nông dân, nông nô cày rồi thu địa tô.</p>\r\n<p>Nông dân lĩnh canh ở phương Đông và nông nô ở châu Âu khi nhận ruộng của địa chủ, lãnh chúa phải nộp một phần hoa lợi cho địa chủ, lãnh chúa gọi là địa tô.</p><p>Lãnh chúa có cuộc sống đầy đủ, xa hoa, có quyền lực tối cao, về ruộng đất. Họ đứng đầu cơ quan pháp luật, thống trị nông nô về mặt tinh thần.</p><p>Nông nô là lực lượng lao động chính nhưng phải sống phụ thuộc vào lãnh chúa, khổ cực và đói nghèo.</p><h3>4.Hình thức bóc lột cơ bản trong xã hội phong kiến là gì?</h3><p>Trả lời:</p><p>Hình thức bóc lột cơ bản trong xã hội phong kiến là bóc lột bằng địa tô.</p><h3>5. Chế độ phong kiến phương Đông và châu Âu giống nhau ở điểm nào?</h3><p>Trả lời:</p><p>Chế độ phong kiến phương Đông và châu Âu giống nhau ở điểm:</p>\r\n<ul><li>Trong xã hội phong kiến, giai cấp địa chủ, lãnh chúa phong kiến là giai cấp thống trị.</li><li>Chúng thiết lập bộ máy nhà nước do vua đứng đầu để bóc lột và đàn áp các giai cấp khác.</li><li>Thể chế nhà nước (do vua đứng đầu) và còn được gọi là chế độ quân chủ. Hầu hết các quốc gia phong kiến đều theo chế độ quân chủ.</li></ul><h3>6. Em hiểu thế nào là chế độ quân chủ?</h3><p>Trả lời:</p><p>Trong xã hội phong kiến, giai cấp địa chủ, lãnh chúa phong kiến là giai cấp thống trị, chúng thiết lập bộ máy nhà nước do vua đứng đầu để bóc lột và đàn áp giai cấp bị trị ( nông nô, nông dân lĩnh canh...). Thể chế nhà nước do vua đứng đầu gọi là chế độ quân chủ.</p><h3>7. Chế độ quân chủ phương Đông và châu Âu có gì giống và khác nhau?</h3><p>Trả lời:</p><p>Giống nhau:</p><ul><li>Về nhà nước phong kiến, các quốc gia phương Đông như phương Tây đều theo chế\r\nđộ quân chủ (do vua đứng đầu)</li></ul><p>Khác nhau:</p><ul><li>Ở phương Đông, nền chuyên chế đã có từ thời cổ đại. Sang xã hội phong kiến, nhà\r\nnước quân chủ chuyên chế ngày càng được hoàn thiện hơn, vua chuyên chế có thêm quyền lực, trở thành Hoàng Đế hay Đại Vương.</li><li>Ở phương Tây, thời Hi Lạp và Rô -ma cổ đại đã có các hình thức dân chủ, cộng hòa và đế chế, thực chất đều là chế độ quân chủ. Chế độ chuyên chế thời cổ đại tiếp tục tồn tại ở thời phong kiến. Song ở giai đoạn đầu, quyền lực của nhà vua rất hạn hẹp, thực chất cũng chỉ là một lãnh chúa lớn mà thôi - đó là chế độ phong kiến phân quyền, quyền hành mới được tập trung trong tay vua.</li></ul><h3>8. Lập bảng so sánh về chế độ phong kiến phương Đông và châu Âu theo mẫu:</h3><p>Trả lời:</p><table><tr><th>Nội dung so sánh</th><th>Xã hội phong kiến phương Đông</th><th>Xã hội phong kiến châu Âu</th></tr><tr><td>Thời kỳ hình thành</td> <td>Từ thế kỉ III TCN đến khoảng thế kỉ X</td><td>Từ thế kỉ V đến khoảng thế kỉ X</td></tr><tr><td>Thời kỳ phát triển</td> <td>Từ thế kỉ X đến thế kỉ XV</td><td>Từ thế kỉ XI đến khoảng thế kỉ XV</td></tr><tr><td>Thời kì khủng hoảng, suy vong</td><td>Từ thế kỉ XVI đến giữa thế kỉ XIX</td><td> Từ thế kỉ XIV đến khoảng thế kỉ XV</td></tr><tr><td>Cơ sở kinh tế</td><td> Nông nghiệp đóng kín trong công xã nông thôn</td><td>Nông nghiệp đóng kín trong lãnh địa</td></tr><tr><td>Giai cấp</td> <td>Địa chủ và nông dân lĩnh canh</td> <td>Lãnh chúa và nông nô</td></tr><tr><td>Nhà nước</td> <td>Quân chủ</td><td>Quân chủ</td></tr></table></div>', 'public/images/noidung/bai13/Thoi-ky-phong-kien-World-History.jpg', 2, NULL, '2018-04-05 17:00:00', NULL),
(15, 8, 'Sự suy vong của chế độ phong kiến', 'Su-suy-vong-cua-che-do-phong-kien', '<p>Phong kiến phản ánh hình thức truyền nối và chiếm hữu đất đai của chế độ quân chủ thời xưa, trong thời quân chủ chuyên chế. Trong nhiều trường hợp, những thời kỳ quân chủ trước kia cũng được gọi là thời kỳ phong kiến. Tuy nhiên, trong thời hiện tại, thể chế về chế độ quân chủ thời nay là chế độ quân chủ lập hiến, cho nên phong kiến chỉ phản ánh một giai đoạn, một thời kỳ hay là một hình thái của chế độ quân chủ.</p>', '<div class=\"post\"><h2>1.Những cuộc phát kiến lớn về địa lý</h2><p>Nguyên nhân: do sản xuất phát triển, cần thị trường, nguyện liệu, vàng bạc.</p>\r\n<p>Điều kiện: sự phát triển của khoa học - kỹ thuật tiến bộ – cho phép đóng tàu lớn và có la bàn để con người có thể vượt trùng dương xa xôi, tìm ra những con đường mới, những vùng đất mới.</p><p>Những cuộc phát kiến địa lí tiêu biểu:</p><ul><li>1487, B. Di-a-xơ vòng qua cực Nam châu Phi đến mũi Hảo Vọng.</li><li>8- 1492, C. Cô-lôm-bô từ Tây Ban Nha đi về hướng tây đến biển Ca-ri-bê, đặt chân đến châu Mỹ</li><li>1497, Va-xcô đơ Ga-ma từ Lix-bon, đến Ca-li-cút (Tây Ấn Độ).</li><li>1519- 1522, Ph.Ma-gien-lan lần đầu tien đã đi vòng quanh Trái Đất.</li></ul><p>Kết quả - ý nghĩa: tìm ra con đường biển mới, vùng đất mới, dân tộc mới. Những cuộc phát kiến địa lí đã góp phần thúc đẩy thương nghiệp châu Âu phát triển, đem lại cho giai cấp tư sản những nguồn nguyên liệu quý giá, những kho vàng bạc, châu báu khổng lồ cũng những vùng đất mênh mông ở châu Á, châu Phi và châu Mĩ.</p>\r\n<h2>2.Sự hình thành chủ nghĩa tư bản ở Châu Âu:</h2> <p>Sau các cuộc phát kiến địa lý, quý tộc, thương nhân châu Âu giàu lên nhanh chóng.</p>\r\n<p>Nhờ có vốn, công nhân làm thuê, các quý tộc, thương nhân lập xưởng sản xuất, công ty thương mại, những đồn điền rộng lớn.</p><p>=> các chủ xưởng, chủ đồn điền, và những thương nhân giàu có đó trở thành giai cấp tư sản.</p><p>Những người làm thuê bị giai cấp tư sản bóc lột sức lao động đến kiệt quệ, trở thành giai cấp vô sản</p><p>Nền sản xuất mới tư bản chủ nghĩa ra đời ngay trong lòng chế độ phong kiến.</p><p>Hình thành quan hệ tư bản chủ nghĩa: giai cấp tư sản và giai cấp vô sản.</p></div>', 'public/images/noidung/bai15/Luoc-do-nhung-cuoc-phat-kien-dia-li.jpg', 1, NULL, '2018-04-05 17:00:00', NULL),
(16, 8, 'Cuộc đấu tranh giai cấp tư sản chống phong kiến thời hậu kỳ Trung đại ở Châu Âu', 'Cuoc-dau-tranh-giai-cap-tu-san-chong-phong-kien-thoi-hau-ky-Trung-dai-o-Chau-Au', '<p>Phục Hưng là một phong trào văn hóa thường được xem là bao phủ giai đoạn từ thế kỷ XV đến thế kỷ XVII, khởi đầu tại Firenze (Ý) vào Hậu kỳ Trung Đại, sau đó lan rộng ra phần còn lại của châu Âu ở những quy mô và mức độ khác nhau. Người ta cũng dùng từ Phục Hưng để chỉ, một cách không nhất quán, thời kỳ lịch sử diễn ra phong trào văn hóa nói trên.</p><p>Với tư cách một phong trào văn hóa, Phục Hưng bao hàm sự nở rộ của các nền văn học tiếng Latin cũng như các tiếng dân tộc, bắt đầu từ sự phục hồi việc nghiên cứu các tư liệu cổ điển, sự phát triển của phép phối cảnh tuyến tính và các kỹ thuật nhằm biểu diễn hiện thực tự nhiên hơn trong mỹ thuật, và một cuộc cải cách giáo dục tiệm tiến nhưng phổ cập.</p>', '<div class=\"post\"><h2>1.Phong trào Văn hóa Phục hưng thế kỷ XIV- XVII</h2><p>Nguyên nhân:<p><ul><li>Chế độ phong kiến kìm hãm sự phát triển của xã hội.</li><li>Giai cấp tư sản mạnh về kinh tế, nhưng không có địa vị xã hội nên họ chống lại chế độ phong kiến, dẫn đến Phong trào Văn hóa Phục hưng thế kỷ XIV- XVII.</li></ul><p>Văn hóa Phục hưng: là sự phục hưng tinh thần của nền văn hóa cổ Hi Lạp và Rô ma, sáng tạo nền văn hóa mới của giai cấp tư sản.</p><p>Phong trào bắt đầu ở I-ta -li -a, sau đó lan rộng sang châu Âu.</p><ul><li>Ra-bơ-le là nhà văn, nhà y học.</li><li>Đê-các-tơ: toán và triết học.</li><li>Lê-ô-na đơ Vanh-xi là họa sĩ, kỹ sư. Những tác phẩm kinh điển của ông như Mona Lisa, Bữa ăn tối cuối cùng và Người Vitruvius là đại diện cho nghệ thuật thời Phục hưng.</li><li>Cô-pec-níc là nhà thiên văn</li><li>Sếch-xpia là soạn kịch vĩ đại.</li></ul><p>Tư tưởng: bằng những tác phẩm của mình, giai cấp tư sản đã:</p><p>Phê phán giáo hội Thiên chúa giáo và phong kiến.</p><p>Đề cao giá trị con người, đề cao khoa học tự nhiên, xây dựng thế giới quan duy vật tiến bộ.</p><p>Biện pháp:</p><ul><li>Phát động quần chúng chống phong kiến.</li><li>Là cuộc cách mạng tiến bộ vĩ đại</li><li>Mở đường cho sự phát triển cao hơn của văn hóa Châu Âu.</li><h2>2.Phong trào Cải cách tôn giáo.</h2><p>Nguyên nhân:</p><ul><li>Giáo hội tăng cường bóc lột nhân dân, lấy kinh thánh làm cơ sở tư tưởng để thống trị nhân dân về mặt tinh thần.</li><li>Giáo hội là thế lực cản trở sự phát triển của tư sản đang lên.</li></ul><p>Nội dung:</p><p>Cải cách tôn giáo của Lu thơ (Đức):</p><ul><li>Phủ nhận vai trò thống trị của Giáo Hội.</li><li>Đòi bãi bỏ lễ nghi phiền toái</li><li>Đòi quay về với giáo lý Ki tô nguyên thủy.</li><li>Cải cách của Lu-thơ nhanh chóng lan sang Thụy Sĩ, Pháp, Anh,...</li></ul><p>Tác động:</p><ul><li>Thúc đẩy, châm ngòi cho khởi nghĩa nông dân ở Đức - cuộc đấu tranh đầu tiên của nông dân dưới ngọn cờ tư sản chống phong kiến ở châu Âu.</li><li>Ki tô bị phân làm 2 giáo phái: đạo Tin lành(tôn giáo cải cách) và Ki tô giáo cũ. Hai giáo phái này mâu thuẫn, xung đột lẫn nhau.</li></ul></div>', 'public/images/noidung/bai16/Tai-sao-thoi-phuc-hung-lai-sinh-ra-nhieu-nguoi-khong-lo-World-History.jpg', 1, NULL, '2018-04-05 17:00:00', NULL),
(17, 9, 'Nhật Bản', 'Nhat-Ban', '<p>Nhật Bản (tiếng Nhật: 日本 Nippon) là một đảo quốc ở vùng Đông Á. Tọa lạc trên Thái Bình Dương, nước này nằm bên rìa phía đông của Biển Nhật Bản, Biển Hoa Đông, Trung Quốc, bán đảo Triều Tiên và vùng Viễn Đông Nga, trải dài từ Biển Okhotsk ở phía bắc xuống Biển Hoa Đông và đảo Đài Loan ở phía nam. Chữ kanji trong quốc hiệu Nhật Bản nghĩa là \"gốc của Mặt Trời\", và người ta thường gọi Nhật Bản bằng biệt danh \"Đất nước Mặt Trời mọc\".</p><p>Giữa thế kỷ XIX, với cuộc Minh Trị Duy Tân do Thiên hoàng Minh Trị đề xướng, Nhật mở cửa triệt để với phương Tây. Chế độ Mạc phủ và các phiên do đại danh đứng đầu bị bãi bỏ, quyền lực được tập trung tối cao trong tay Thiên hoàng. Năm 1868, Thiên hoàng Minh Trị dời đô từ Kyōto về Tōkyō. Công nghiệp hóa diễn ra mạnh mẽ, đất nước phát triển, xâm chiếm Đài Loan, Lưu Cầu, đánh bại nhà Thanh, đế quốc Nga trong Chiến tranh Thanh-Nhật và Chiến tranh Nga-Nhật, xâm lược Triều Tiên...</p>', '<div class=\"post\"><h2>1.Nhật Bản từ nửa đầu thế kỉ XIX đến trước năm 1868</h2><p>Đầu thế kỉ XIX chế độ Mạc phủ ở Nhật Bản đứng đầu là Tướng quân (Sô- gun) làm vào khủng hoảng suy yếu.</p><p>Về kinh tế</p><ul><li>Nông nghiệp vẫn dựa trên quan hệ sản xuất phong kiến lạc hậu. Địa chủ bóc lột nhân dân lao động rất nặng nề. Tình trạng mất mùa đói kém liên tiếp xảy ra.</li><li>Công nghiệp :ở  các thành thị, hải cảng, kinh tế hàng hoá phát triển, công trường thủ công xuất hiện ngày càng nhiều.</li><li>Những mầm mống kinh tế tư sản chủ nghĩa phát triển nhanh chóng.</li></ul><p>Về xã hội</p><ul><li>Tầng lớp tư sản thương nghiệp ra đời từ lâu, tầng lớp tư sản công nghiệp hình thành và ngày càng giầu có.</li><li>Các nhà công thương lại không có quyền lực về chính trị.</li><li>Giai cấp tư sản vẫn còn yếu, không đủ sức xoá bỏ chế độ phong kiến, nông dân là đối tượng bóc lột chủ yếu của giai cấp phong kiến, còn thị dân thì không chỉ bị phong kiến khống chế mà còn bị các nhà buôn và bọn cho vay lãi bóc lột.</li></ul><p>Về chính trị</p><ul><li>Đến giữa thế kỉ XIX, Nhật Bản vẫn là quốc gia phong kiến. Nhà vua được tôn là Thiên hoàng, có vị tối cao nhưng quyền hành chủ yếu thuộc về Tướng quân.</li><li>Giữa lúc mâu thuẫn giai cấp trong nước ngày càng gay gắt, chế độ Mạc Phủ khủng hoảng nghiêm trọng thì các nước tư bản phương Tây, trước tiên là Mĩ, dùng áp lực quân sự đòi Nhật Bản phải “mở cửa”.</li><li>Như vậy, đến giữa thế kỉ XIX, Nhật Bản đã lâm vào một cuộc khủng hoảng trầm trọng, đứng trước sự lựa chọn: hoặc tiếp tục con đường trì trệ, bảo thủ để các nước đế quốc xâu xé; hoặc canh tân, cải cách xoá bỏ chế độ phong kiến, đưa Nhật Bản hoà nhập với nền kinh tế phương Tây.</li></ul><img class=\"img-responsive\" src=\"public/images/noidung/bai12/Ban-tay-cua-Perry-cua-My-vuon-toi-nuoc-Nhat-World-History.jpg\"></img><blockquote>Hình 17.1: Bàn tay của Perry của Mỹ vươn tới nước Nhật</blockquote><h2>2.Cuộc Duy tân Minh Trị</h2><img class=\"img-responsive\" src=\"public/images/noidung/bai12/Minh-Tri-World-History.jpg\"></img><blockquote>Hình 17.2: Minh Trị</blockquote><p>Nguyên nhân</p><ul><li> Những hiệp ước bất bình mà Mạc phủ kí kết với nước ngoài làm cho tầng lớp xã hội phản ứng mạnh mẽ.</li><li>Phong trào đấu tranh chống Sô gun  nổ ra sôi nổi vào những năm 60 của thế kỉ XX đã làm sụp đổ chế độ Mạc phủ.</li><li>Tháng 01/1868 Sô-gun bị lật đổ. Thiên hoàng Minh Trị (Meiji) trở lại nắm quyền và thực hiện một loạt cải cách.</li></ul><p>Nội dung cải cách Minh Trị</p><p>Tháng 1-1868, sau khi lên ngôi, Thiên hoàng Minh Trị (May-gi) đã thực hiện một loạt cải cách tiến bộ nhằm đưa Nhật Bản thoát khỏi tình trạng phong kiến lạc hậu.</p><p>Về chính trị</p><ul><li>Nhật hoàng tuyên bố thủ tiêu chế độ Mạc phủ, lập chính phủ mới, thực hiện bình đẳng ban bố quyền tự do.</li><li>Ban hành Hiến pháp 1889.</li></ul><p>Về kinh tế</p><ul><li>Thống nhất tiền tệ, thống nhất thị trường, xoá bỏ sự độc quyền ruộng đất của giai cấp phong kiến.</li><li>Tăng cường phát triển kinh tế tư bản chủ nghĩa ở nông thôn.</li><li>Xây dựng cơ sở hạ tầng, đường sá, cầu cống, phục vụ giao thông liên lạc.</li></ul><p>Về quân sự</p><ul><li>Được tổ chức huấn luyện theo kiểu phương Tây.</li><li>Chế độ nghĩa vụ quân sự thay cho chế độ trưng binh.</li><li> Chú trọng đóng tàu  chiến, sản xuất vũ khí đạn dược.</li></ul><p>Về giáo dục</p><ul><li>Thi hành chính sách giáo dục bắt buộc.</li><li>Chú trọng nội dung  khoa học- kỹ thuật  trong chương trình giảng dạy,...</li><li>Cử những học sinh giỏi đi du học phương Tây…</li></ul><p>Tính chất - ý nghĩa</p><ul><li>Cải cách Minh Trị mang tính chất của một cuộc cách mạng tư sản.</li><li>Nhật thoát khỏi số phận bị các nước tư bản phương Tây xâm lược.</li><li>Mở đường cho chủ nghĩa tư bản phát triển ở Nhật.</li></ul><img class=\"img-responsive\" src=\"public/images/noidung/bai12/Mot-cong-xuong-cua-Nhat-theo-cong-nghe-phuong-Tay-World-History.jpg\"></img><blockquote>Hình 17.3: Một công xưởng của Nhật theo công nghệ phương Tây khoảng thập niên 1880</blockquote><h2>3.Nhật Bản chuyển sang giai đoạn đế quốc chủ nghĩa</h2><p>Trong 30 năm cuối thế kỉ XIX  (sau chiến tranh Trung - Nhật (1894-1895), kinh tế Nhật Bản phát triển mạnh mẽ.</p><p>Quá trình tập trung trong công nghiệp, thương nghiệp với ngân hàng đã đưa đến sự ra đời những công ty độc quyền, Mít-xưi, Mit-su-bi-si chi phối đời sống kinh tế, chính trị Nhật Bản.</p><p>Đầu thế kỉ XX, Nhật thi hành chính sách xâm lược và bành trướng:</p><ul><li>Năm 1874 Nhật xâm lược Đài Loan.</li><li>Năm 1894 – 1895 Nhật gây chiến với Trung Quốc để tranh giành Triều Tiên, uy hiếp Bắc Kinh, chiếm cửa biển Lữ Thuận, nhà Thanh phải nhượng Đài Loan và Liêu Đông cho Nhật.</li><li>Năm 1904-1905 Nhật gây chiến với Nga buộc Nga phải nhường cửa biển Lữ Thuận, đảo Xa-kha-lin, thừa nhận Nhật Bản chiếm đóng Triều Tiên.</li><li>Năm 1914, Nhật dùng vũ lực mở rộng ảnh hưởng ở Trung Quốc và chiếm Sơn Đông. Nhật trở thành đế quốc hùng mạnh nhất châu Á.</li></ul><p>Cùng với sự phát triển của chủ nghĩa tư bản là sự bần cùng hoá của quần chúng nhân dân lao động.</p><p>Chủ nghĩa đế quốc Nhật được gọi là “Chủ nghĩa đế quốc phong kiến quân phiệt, hiếu chiến”</p><img class=\"img-responsive\" src=\"public/images/noidung/bai12/De-quoc-Nhat-Ban-World-History.jpg\"></img><blockquote>Hình 17.4: Đế quốc Nhật Bản cuối thế kỷ 19 đầu thế kỷ XX</blockquote><p>Chính sách đối nội</p><p>Rất phản động, bóc lột nặng nề nhân dân trong nước, nhất là giai cấp công nhân, công nhân Nhật phải làm việc từ 12 đến 14 giờ một ngày trong những điều kiện tồi tệ, tiền lương thấp.</p><p>Sự bóc lột nặng nề của giới chủ đã dẫn đến nhiều cuộc đấu tranh của công nhân.</p></div>', 'public/images/noidung/bai17/Nhat-Ban-World-History.jpg', 4, NULL, '2018-04-05 17:00:00', NULL),
(18, 9, 'Ấn Độ', 'An-Do', '<p>Các sử gia xem thời kỳ hiện đại của Ấn Độ bắt đầu từ giai đoạn 1848-1885. Việc bổ nhiệm James Broun-Ramsay làm Toàn quyền của Công ty Đông Ấn Anh vào năm 1848 chuẩn bị cho những thay đổi cốt yếu đối với một quốc gia hiện đại. Chúng bao gồm củng cố và phân ranh giới chủ quyền, sự giám sát của người dân, và giáo dục cho công dân. Các biến đổi về công nghệ như đường sắt, kênh đào, và điện báo được đưa đến Ấn Độ không lâu sau khi chúng được giới thiệu tại châu Âu. Tuy nhiên, sự bất mãn đối với Công ty cũng tăng lên trong thời kỳ này, và Khởi nghĩa Ấn Độ 1857 bùng nổ. Cuộc khởi nghĩa bắt nguồn từ những oán giận và nhận thức đa dạng, bao gồm cải cách xã hội kiểu Anh, thuế đất khắc nghiệt, và đối đãi tồi của một số địa chủ giàu có và phiên vương, nó làm rung chuyển nhiều khu vực ở bắc bộ và trung bộ Ấn Độ và làm lung lay nền móng của Công ty Đông Ấn Anh</p>', '<div class=\"post\"><h2>1.Tình hình kinh tế, xã hội Ấn Độ nửa sau thế kỉ XIX</h2><p>Quá trình thực dân xâm lược Ấn Độ</p><p>Từ đầu thế kỉ XVII chế độ phong kiến Ấn Độ suy yếu ,các nước phương Tây chủ yếu Anh - Pháp đua nhau xâm lược.</p><p>Kết quả: Giữa thế kỉ XVII Anh hoàn thành xâm lược và đặt ách cai trị Ấn Độ.</p><p>Chính sách cai trị của thực dân Anh</p><p>Về kinh tế</p><ul><li>Thực dân Anh mở rộng công cuộc khai thác Ấn Độ với quy mô rộng lớn.</li><li>Ra sức vơ vét các nguồn nguyên liệu và bóc lột công nhân rẻ mạt để thu lợi nhuận. Ấn Độ trở thành thuộc địa quan trọng nhất nền công nghiệp Anh.</li></ul><img class=\"img-responsive\" src=\"public/images/noidung/bai19/Nhung-nan-nhan-cua-nan-doi-World-History.jpg\"></img><blockquote>Hình 18.1: Những nạn nhân của nạn đói 1876-1877.</blockquote><p>Về chính trị - xã hội</p><ul><li>Chính phủ Anh nắm quyền cai trị trực tiếp Ấn Độ.</li><li>Thực dân Anh đã thực  hiện chính sách chia để trị, mua chuộc tầng lớp có thế lực trong giai cấp phong kiến bản xứ.</li><li>Anh còn tìm cách khơi sâu sự cách biệt về chủng tộc, tôn giáo và đẳng cấp trong xã hội để dễ bề cai trị.</li></ul><p>Về văn hóa - giáo dục: thi hành chính sách giáo dục ngu dân, khuyến khích tập quán lạc hậu và hủ tục cổ xưa.</p><p>Hậu quả</p><ul><li>Kinh tế giảm sút, bần cùng</li><li>Đời sống nhân dân người dân cực khổ</li></ul><img class=\"img-responsive\" src=\"public/images/noidung/bai19/Nu-hoang-Victoria-World-History.jpg\"></img><blockquote>Hình 18.2: Nữ hoàng Victoria trở thành Nữ hoàng Ấn Độ</blockquote><img class=\"img-responsive\" src=\"public/images/noidung/bai19/Luoc-do-phong-trao-cach-mang-o-An-Do-World-History.jpg\"></img><blockquote>Hình 18.3: Lược đồ phong trào cách mạng ở Ấn Độ cuối thế kỷ XIX-đầu thế kỷ XX</blockquote><p>2.Cuộc khởi nghĩa Xi-pay (1857-1859)</p><p>Nguyên nhân</p><ul><li>Do tinh thần dân tộc, lòng yêu nước, ý chí đấu tranh chống sự thống trị của thực dân Anh của nhân dân Ấn Độ</li><li>Do binh lính Xi-pay bị sĩ quan Anh bạc đãi, khinh rẽ,tinh thần dân tộc và tín ngưỡng bị xúc phạm nên bất mãn nổi dậy đấu tranh.</li></ul><p>Diễn biến</p><ul><li>Sáng ngày 10/05/1857, ở Mi-rút (gần Đê-li), khi thực dân Anh sắp áp giải 85 binh lính Xi-pay trái lệnh, thì ba trung đoàn Xi-pay nổi dậy khởi nghĩa bắt bọn chỉ huy Anh.</li><li>Nông dân các vùng lân cận cũng tham gia nghĩa quân, vây bắt chỉ huy Anh.</li><li>Khởi nghĩa nhanh chóng lan rộng đến Đê-li, khắp miền Bắc và một phần miền Tây Ấn Độ.</li><li>Nghĩa quân đã lập được chính quyền ở 3 thành phố lớn.</li><li>Cuộc khởi nghĩa duy trì được 2 năm thì bị thực dân Anh dốc toàn bộ lực lượng đàn áp dã man. Nhiều nghĩa quân bị quân Anh trói vào họng súng đại bác, rồi bắn cho tan xương nát thịt. Khởi nghĩa bị thất bại.</li></ul><p>Ý nghĩa</p><p>Thể hiện lòng yêu nước, tinh thần đấu tranh bất khuất.</p><p>Ý thức vươn tới độc lập của nhân dân Ấn Độ.</p><img class=\"img-responsive\" src=\"public/images/noidung/bai19/Khoi-nghia-Xi-pay-World-History.jpg\"></img><blockquote>Hình 18.4: Khởi nghĩa Xi-pay</blockquote><img class=\"img-responsive\" src=\"public/images/noidung/bai19/Thuc-dan-Anh-dan-ap-World-History.jpg\"></img><blockquote>Hình 18.5: Thực dân Anh đàn áp</blockquote><h2>3.Đảng Quốc đại và phong trào dân tộc (1885-1908)</h2><h4>a.Đảng Quốc đại</h4><p>Từ giữa thế kỉ XIX, giai cấp tư sản và tầng lớp trí thức Ấn Độ đã dần dần đóng vai trò quan trọng.</p><p>Tư sản Ấn Độ muốn được tự do phát triển kinh tế và đòi hỏi được tham gia chính quyền, nhưng bị thực dân Anh kìm hãm.</p><p>Cuối năm 1885, Đảng Quốc dân Đại hội (Quốc đại) thành lập. Đó là chính Đảng đầu tiên của của giai cấp tư sản Ấn Độ. Nó đánh dấu một giai đoạn mới, giai đoạn giai cấp tư sản Ấn Độ bước lên đài chính trị.</p><p>Trong 20 năm đầu (1885-1905), Đảng Quốc đại chủ trương đấu tranh ôn hoà để đòi hỏi chính phủ thực dân tiến hành cải cách và không tán thành phương pháp đấu tranh bằng vũ lực. Do thái độ thỏa hiệp của những người cầm đầu và chính quyền sách 2 mặt của chính quyền Anh, nội bộ Đảng Quốc đại bị phân hóa thành 2 phái: ôn hòa và phái cực đoan (kiên quyết chống Anh do Ti-lắc đứng đầu)</p><img class=\"img-responsive\" src=\"public/images/noidung/bai19/Ti-Lac-World-History.jpg\"></img><blockquote>Hình 18.6: Ti Lắc</blockquote><h4>b.Phong trào dân tộc</h4><p>Tháng 7/1905, chính quyền Anh thi hành chính sách chia để trị. Ban hành đạo luật chia đôi xứ Ben-gan: miền Đông theo đạo Hồi và miền Tây theo đạo Ấn. Điều đó làm bùng nổ lên phong trào đấu tranh chống thực dân Anh, đặc biệt ở Bom-bay và Can-cút-ta.</p><p>Ngày 6/10/1905, đạo luật chia cắt Ben-gan bắt đầu có hiệu lực, nhân dân coi đó là ngày quốc tang: hơn 10 vạn người kéo đến bờ sông Hằng, làm lễ tuyên thệ và hát vang bài “Kính chào Người – Mẹ hiền Tổ quốc” để tỏ ý đoàn kết, thống nhất. Khắp nơi vang lên khẩu hiệu “Ấn Độ là của người Ấn Độ”.</p><p>Tháng 6/1908, thực dân Anh bắt Ti-lắc và tuyên án ông 6 năm tù. Vụ án Ti-lắc thổi bùng lên một đợt đấu tranh mới. Hàng vạn công nhân Bom-bay tiến hành tổng bãi công 6 ngày (để trả lời 6 năm tù của Ti-lắc), xây dựng chiến luỹ, thành lập các đơn vị chiến đấu chống lại quân Anh. Các thành phố khác cũng hưởng ứng, cuộc đấu tranh lên đỉnh cao buộc thực dân Anh phải thu hồi đạo luật chia cắt Ben-gan.</p><p>Cao trào cách mạng 1905-1908 mang đậm ý thức dân tộc đánh dấu sự thức tỉnh của nhân dân Ấn Độ.</p><img class=\"img-responsive\" src=\"public/images/noidung/bai19/Bengal-bị-chia-cat-World-History.jpg\"></img><blockquote>Hình 18.7: Bengal bị chia cắt năm 1905</blockquote></div>', 'public/images/noidung/bai19/An-Do-World-History.jpg', 3, NULL, '2018-04-05 17:00:00', NULL),
(19, 9, 'Trung Quốc', 'Trung-Quoc', '<p>Ngày 1 tháng 1 năm 1912, Trung Hoa Dân Quốc được thành lập, Tôn Trung Sơn của Quốc dân đảng được tuyên bố là đại tổng thống lâm thời. Tuy nhiên, sau đó chức đại tổng thống được trao cho cựu đại thần của triều Thanh là Viên Thế Khải, nhân vật này tuyên bố bản thân là hoàng đế của Trung Quốc vào năm 1915. Do đối diện với chỉ trích và phản đối rộng khắp trong quân Bắc Dương của mình, Viên Thế Khải buộc phải thoái vị và tái lập chế độ cộng hòa.</p>', '<div class=\"post\"><img class=\"img-responsive\" src=\"public/images/noidung/bai20/Hoa-uoc-Nam-Kinh-World-History.jpg\"></img><blockquote>Hình 20.1: Hoà ước Nam Kinh 1842</blockquote><h2>1.Trung Quốc bị các đế quốc xâm lược</h2><p>Nguyên nhân Trung Quốc bị xâm lược</p><ul><li>Thế kỉ XVIII đầu XIX các nước tư bản phương Tây tăng cường xâm chiếm thị trường thế giới.</li><li>Trung Quốc là một thị trường lớn, béo bở, chế độ đang suy yếu  nên trở thành đối tượng xâm lược của nhiều đế quốc.</li><li>Chế độ phong kiến Mãn Thanh đang suy yếu.</li></ul><p>Quá trình đế quốc xâm lược Trung Quốc</p><ul><li>Thế kỉ XVIII các đế quốc dùng mọi thủ đoạn, tìm cách ép chính quyền Mãn Thanh phải “mở cửa”, cắt đất.</li><li>Đi đầu là thực dân Anh:<ul><li>Anh thực hiện “Chiến tranh thuốc phiện”(6-1840 đến 8-1842)</li><li>Chúng đã buộc nhà Thanh phải ký Hiệp ước Nam Kinh Năm 1842, chấp nhận các điều khoản thiệt thòi (bồi thường chiến phí, nhượng Hồng Kông , mở 5 cửa biển…)</li></ul></li><li>Đi sau  Anh, các nước khác đua nhau xâu xé Trung Quốc:<ul><li>Đức chiếm Sơn Đông.</li><li>Anh chiếm châu thổ sông Dương Tử.</li><li>Pháp chiếm Vân Nam, Quảng Tây,Quảng Đông.</li><li>Nga - Nhật Bản chiếm vùng Đông Bắc.</li></ul></li></ul><p>Hậu quả: xã hội Trung Quốc nổi lên 2 mâu thuẫn cơ bản:</p><ul><li>Nhân dân Trung Quốc với đế quốc.</li><li>Nông dân với phong kiến.</li><li>Dẫn  đến phong trào đấu tranh chống phong kiến, đế quốc.</li></ul><img class=\"img-responsive\" src=\"public/images/noidung/bai20/Chien-tranh-thuoc-phien-World-History.jpg\"></img><blockquote>Hình 20.2: Chiến tranh thuốc phiện năm 2040</blockquote><img class=\"img-responsive\" src=\"public/images/noidung/bai20/Cac-de-quoc-chia-se-TQ-World-History.jpg\"></img><blockquote>Hình 20.3: Các đế quốc chia sẻ Trung Quốc</blockquote><img class=\"img-responsive\" src=\"public/images/noidung/bai20/Su-phan-chia-vung-anh-huong-o-TQ-World-History.jpg\"></img><blockquote>Hình 20.4: Sự phân chia vùng ảnh hưởng ở Trung Quốc của các đế quốc.</blockquote><h2>2.Phong trào đấu tranh của nhân dân Trung Quốc giữa thế kỉ XIX đến đầu thế kỉ XX</h2><h4>a.Khởi nghĩa Thái bình Thiên Quốc</h4><p>Diễn biến:Bùng nổ ngày 1/1/1851 tại kim Điền (Quảng Tây) ,lan rộng khắp cả nước, bị phong kiến đàn áp, năm 1864 thất bại.</p><p>Lãnh đạo: Hồng Tú Toàn</p><p>Lực lượng: Nông dân</p><p>Tính chất: là cuộc khởi nghĩa nông dân vĩ đại chống phong kiến làm lung lay triều đình phong kiến Mãn Thanh.</p><img class=\"img-responsive\" src=\"public/images/noidung/bai20/Mot-cuoc-hop-cua-nghia-quan-Thai-Binh-Thien-Quoc-World-History.jpg\"></img><blockquote>Hình 20.5: Một cuộc họp của nghĩa quân Thái Bình Thiên Quốc</blockquote><h4>b. Phong trào Duy Tân 1898</h4><p> Diễn biến: Năm 1898 diễn ra cuộc vận động Duy Tân, tiến hành cải cách cứu vãn tình thế.</p><p>Lãnh đạo: Khang Hữu Vi, Lương Khải Siêu.</p><p>Lực lượng: quan lại, sỹ phu tiến bộ, vua Quang Tự.</p><p>Tính chất: Cải cách dân chủ, tư sản, khởi xướng khuynh hướng dân chủ tư sản ở Trung Quốc, chỉ tồn tại 100 ngày.</p><img class=\"img-responsive\" src=\"public/images/noidung/bai20/Khang-Huu-Vi-Luong-Khai-Sieu-World-History.jpg\"></img><blockquote>Hình 20.6: Khang Hữu Vi, Lương Khải Siêu.</blockquote><h4>c.Phong trào Nghĩa Hòa đoàn</h4><p>Diễn biến: Năm 1899 bùng nổ ở Sơn Đông lan sang Trực Lệ, Sơn Tây, tấn công sứ quán nước ngoài ở Bắc Kinh.</p><p>Bị liên quân 8 nước đế quốc tấn công  nên thất bại.</p><p>Lực lượng: Nông dân.</p><p>Tính chất: Phong trào yêu nước chống đế quốc. Giáng một đòn mạnh vào đế quốc.</p><p>Nguyên nhân thất bại</p><ul><li>Chưa có tổ chức lãnh đạo</li><li>Do sự bảo thủ, hèn nhát của triều đình phong kiến.</li><li>Do phong kiến và đế quốc cấu kết đàn áp</li></ul><img class=\"img-responsive\" src=\"public/images/noidung/bai20/Luoc-do-phong-trao-Nghia-Hoa-Doan-World-History.jpg\"></img><blockquote>Hình 20.7: Lược đồ phong trào Nghĩa Hoà Đoàn.</blockquote><table><tr><th>Nội dung</th><th>Khởi nghĩa Thái bình Thiên Quốc</th><th>Phong trào Duy Tân</th><th>Phong trào Nghĩa Hòa đoàn</th></tr><tr><td>Diễn biến chính</td><td>Bùng nổ ngày 1/1/1851 tại kim Điền (Quảng Tây), lan rộng khắp cả nước.<br>Bị phong kiến đàn áp<br>Năm 1864 thất bại</td><td>Năm 1898 diễn ra cuộc vận động Duy Tân, tiến hành cải cách cứu vãn tình thế.<br>Diễn ra 100 ngày</td><td>Năm 1899 bùng nổ ở Sơn Đông lan sang Trực Lệ, Sơn Tây, tấn công sứ quán nước ngoài ở Bắc Kinh, bị liên quân 8 nước đế quốc tấn công  nên thất bại</td></tr><tr><td>Lãnh đạo</td><td>Hồng Tú Toàn</td><td>Khang Hữu Vi</br>Lương Khải Siêu</td><td></td></tr><tr><td>Lực lượng</td><td>Nông dân</td><td>Quan lại, sỹ phu tiến bộ, vua Quang Tự</td><td>Nông dân</td></tr><tr><td>Tính chất - ý thức</td><td>Là cuộc khởi nghĩa nông dân vĩ đại chống phong kiến làm lung lay triều đình phong kiến Mãn Thanh</td><td>Cải cách dân chủ, tư sản, khởi xướng khuynh hướng dân chủ tư sản ở Trung Quốc</td><td>Phong trào yêu nước chống đế quốc. Giáng một đòn mạnh vào đế quốc.</td></tr></table><h2>3.Tôn Trung Sơn và cách mạng Tân Hợi 2011</h2><img class=\"img-responsive\" src=\"public/images/noidung/bai20/Luoc-do-cach-mang-Tan-Hoi-World-History.jpg\"></img><blockquote>Hình 20.8: Lược đồ Cách mạng Tân Hợi</blockquote><p>Tôn Trung Sơn và Đồng minh hội</p><p>Tôn Trung Sơn là một trí thức có tư tưởng cách mạng theo khuynh hướng dân chủ tư sản.</p><p>Tháng 8/2005 Tôn Trung Sơn tập hợp giai cấp tư sản Trung Quốc thành lập Đồng minh hội- chính Đảng của giai cấp tư sản Trung Quốc.</p><p>Cương lĩnh chính trị: theo chủ nghĩa Tam Dân của Tôn Trung Sơn.</p><p>Mục tiêu: lật đổ Mãn Thanh, thành lập dân quốc,thực hiện bình đẳng về ruộng đất ..bình quân địa quyền.</p><p>Lực lượng : trí thức tư sản, tiểu tư sản ,địa chủ , thân sĩ bất bình với nhà Thanh.</p><p>Cách mạng Tân Hợi 2011</p><p>Nguyên nhân</p><ul><li>Nhân dân Trung Quốc mâu thuẫn với đế quốc phong kiến</li><li>Ngòi nổ của cách mạng là do nhà Thanh trao quyền kiểm soát đường sắt cho đế quốc nên phong trào “giữ đường” bùng nổ, nhân cơ hội đó Đồng minh hội phát động đấu tranh.</li><li>Khởi nghĩa bùng nổ ở Vũ Xương 10/10/2011, lan rộng khắp miền Nam, miền Trung.</li><li>Ngày 20/12/2011 Tôn Trung Sơn làm Đại Tổng thống lâm thời, tuyên bố thành lập chính phủ lâm thời Trung Hoa dân quốc.</li><li>Trước thắng lợi của cách mạng, tư sản thương lượng với nhà Thanh, đế quốc can thiệp.</li><li>Kết quả: Vua Thanh thoái vị, Tôn Trung Sơn từ chức, Viên Thế Khải làm Tổng thống.</li></ul><img class=\"img-responsive\" src=\"public/images/noidung/bai20/Khoi-nghia-Vu-Xuong-World-History.jpg\"></img><blockquote>Hình 20.9: Khởi nghĩa Vũ Xương bắt đầu cuộc Cách Mạng Tân Hợi, 2011</blockquote><img class=\"img-responsive\" src=\"public/images/noidung/bai20/Quan-nha-Man-Thanh-dau-hang-luc-luong-cach-mang-World-History.jpg\"></img><blockquote>Hình 20.10: Quân Nhà  Mãn Thanh đầu hàng lực lượng cách mạng</blockquote><p>Tính chất - ý nghĩa</p><ul><li>Tính chất cuộc cách mạng tư sản không trịêt để.</li><li>Lật đổ phong kiến, mở đường cho chủ nghĩa tư bản phát triển, ảnh hưởng đến Châu Á.</li></ul><p>Hạn chế</p><ul><li>Không thủ tiêu thực sự giai cấp phong kiến.</li><li>Không đụng chạm đến các nước đế quốc xâm lược.</li><li>Không giải quyết vấn đề ruộng đất cho nông dân.</li></ul><img class=\"img-responsive\" src=\"public/images/noidung/bai20/Ton-Trung-Son-World-History.jpg\"></img><blockquote>Hình 20.11: Tôn Trung Sơn</blockquote></div>', 'public/images/noidung/bai20/Trung-Quoc-World-History.jpg', 5, NULL, '2018-04-05 17:00:00', NULL);
INSERT INTO `sukien` (`MaSK`, `MaCTGD`, `TieuDe`, `TieuDeKhongDau`, `TomTatSK`, `NoiDung`, `HinhSK`, `SoLuotXem`, `Video`, `created_at`, `updated_at`) VALUES
(20, 9, 'Các nước Đông Nam Á', 'Cac-nuoc-Dong-Nam-A', '<p>rong những năm giữa hai cuộc chiến, những phong trào quốc gia đó lớn mạnh và xung đột với các chính quyền thuộc địa khi họ yêu cầu tự quyết. Sự chiếm đóng của Nhật Bản trong Chiến tranh thế giới thứ hai là bước ngoặt quyết định cho các phong trào đó. Nhật Bản phá vỡ tính bí hiểm của sự siêu việt của người da trắng và đã kích thích các phong trào đó.</p><p>Với sự phục hồi của các phong trào quốc gia, người châu Âu đã đối mặt với một Đông Nam Á hoàn toàn khác sau cuộc chiến. Indonesia tuyên bố độc lập ngày 17 tháng 8 1945 và sau đó tiến hành một cuộc chiến ác liệt chống lại những người Hà Lan đang tìm cách quay trở lại....</p>', '<div class=\"post\"><h2>1.Quá trình xâm lược của chủ nghĩa thực dân vào các nước Đông Nam Á</h2><p>Nguyên nhân</p><p>Các nước tư bản cần thị trường, thuộc địa nên đẩy mạnh xâm lược thuộc địa.</p><ul><li>Đông Nam Á có vị trí chiến lược quan trọng.</li><li>Giàu tài nguyên thiên nhiên, có nền văn hóa lâu đời.</li><li>Chế độ phong kiến khủng hoảng. Kinh tế kém phát triển.</li><li>Khủng hoảng triền miên về chính trị, kinh tế, xã hội.</li><p>Quá trình xâm lược</p><table><tr><th>Tên các nước Đông Nam Á</th><th>Thực dân xâm lược</th><th>Thời gian hoàn thành xâm lược</th></tr><tr><td>In-đô-nê-xi-a</td><td>Bồ Đào Nha, Tây Ban Nha, Hà Lan</td><td>Giữa XIX Hà Lan hoàn thành xâm chiếm và lập ách thống trị</td></tr><tr><td>Phi-lip-pin</td><td>Tây Ban Nha, Mĩ</td><td>Giữa thế kỉ XVI Tây Ban Nha thống trị<br>Năm 1898 Mĩ chiến tranh với Tây Ban Nha, hất cẳng Tây Ban Nha khỏi Phi-lip-pin.<br>Năm 1899-1902 Mĩ chiến tranh  xâm lược  Philíppin, biến quần đảo, này thành thuộc điạ của Mĩ.</td></tr><tr><td>Miến Điện</td><td>Anh</td><td>Năm 1885 Anh thôn tính Miến Điện.</td></tr><tr><td>\r\nMa-lai-xi-a</td><td>Anh</td><td>Đầu thế kỉ XIX Mã - lai trở thành thuộc địa của Anh</td></tr><tr><td>Việt Nam - Lào- Cam-pu-chia</td><td>Pháp</td><td>Cuối thế kỉ XIX, Pháp hoàn thành xâm lược 3 nước Đông Dương</td></tr><tr><td>Xiêm (Thái Lan)</td><td>Anh - Pháp tranh chấp</td><td>Xiêm vẫn giữ được độc lập</td></tr></table><h2>.Phong trào chống thực dân Hà Lan của nhân dân In-đô-nê-xi-a</h2><p>Chính sách thống trị thực dân Hà Lan đã làm bùng nổ nhiều cuộc đấu tranh giải phóng dân tộc.</p><p>1825-1830: Cuộc khởi nghĩa A - chê do hoàng tử Di-pô-nê-gô-rô  vương quốc Yogyacata lãnh đạo,được đông đảo nhân dân trên đảo Giava và các đảo khác đi theo,  là cuộc nổi dậy lớn nhất của người Inđônêxia hồi đầu thế kỉ XIX.</p><p>Cuộc khởi nghĩa nông dân do Sa-min lãnh đạo năm 1890</p><p>Các tổ chức chính trị của công nhân ra đời như: Hiệp hội công nhân đường sắt (1905), Hiệp hội công nhân xe lửa (1908). Tháng 12/1914, Liên minh xã hội dân chủ Inđônêxia ra đời nhằm tuyên truyền  chủ nghĩa Mác trong công nhân đặt cơ sở cho Đảng Cộng sản ra đời (5/1920). Giai cấp tư sản dân tộc, tầng lớp trí thức tiếp thu tư tưởng dân chủ tư sản châu Âu, đóng vai trò nhất định trong phong trào yêu nước ở Inđônêxia đầu thế kỉ XX. Vì vậy phong trào yêu nước mang màu sắc mới theo khuynh hướng dân chủ tư sản với sự tham gia của công nhân và tư sản.</p><img class=\"img-responsive\" src=\"public/images/noidung/bai21/Pageran-Diponegoro-World-History.jpg\"></img><blockquote>Hình 21.1: Pangeran Diponegoro lãnh đạo phong trào cách mạng ở Indonesia 1825-1830</blockquote><img class=\"img-responsive\" src=\"public/images/noidung/bai21/Khoi-nghia-A-che-World-History.jpg\"></img><blockquote>Hình 21.2: Khởi nghĩa A – chê</blockquote><h2>3.Phong trào chống thực dân ở Phi-lip-pin</h2><p>Nguyên nhân</p><ul><li>Thực dân Tây Ban Nha đặt ách  thống trị  trên 300 năm ở Philíppin, khai thác bóc lột triệt để tài nguyên và sức lao động.</li><li>Mâu thuẫn giữa nhân dân Philíppin  và thực dân Tây Ban Nha ngày càng gay gắt dẫn đến  phong trào đấu tranh bùng nổ.</li></ul><p>Phong trào đấu tranh</p><ul><li>Năm 1872 có khởi nghĩa ở Ca-vi-tô, nghĩa quân làm chủ Ca-vi-tô được 3 ngày thì thất bại.</li><li>Vào những năm 90 của thế kỉ XIX, ở Philíppin xuất hiện 2 xu hướng chính trong phong trào giải phóng dân tộc.</li></ul><table><tr><th>Nội dung</th><th>Xu hướng cải cách</th><th>Xu hướng bạo động</th></tr><tr><td>Lãnh đạo</td><td>Hô-xê-Ri-dan</td><td>Bô-ni-pha-xi-ô</td></tr><tr><td>Lực lượng tham gia</td><td>“Liên minh Philíppin”, bao gồm trí thức yêu nước, địa chủ, tư sản tiến bộ, một số hộ nghèo</td><td>“Liên hiệp những người con yêu quý của nhân dân” tập hợp chủ yếu là nông dân, dân nghèo thành thị</td></tr><tr><td>Hình thức đấu tranh</td><td>Đấu tranh ôn hòa</td><td>Khởi nghĩa, vũ trang tiêu biểu là  cuộc khởi nghĩa tháng 8/1896</td></tr><tr><td>Chủ trương đấu tranh</td><td>Tuyên truyền, khơi dậy ý thức dân tộc, đòi quyền bình đẳng với người Tây Ban Nha.</td><td>Đấu tranh lật đổ ách thống trị của Tây Ban Nha, xây dựng quốc gia độc lập.</td></tr><tr><td>Kết quả - ý nghĩa</td><td>Tuy thất bại nhưng Liên minh đã thức tỉnh, tinh thần dân tộc, chuẩn bị tư tưởng cho cao trào cách mạng sau này</td><td>Khởi nghĩa tháng 8/1896 đã  giải phóng nhiều vùng, thành lập được chính quyền nhân dân, tiến tới thành lập nền cộng hòa.</td></tr></table><p>Phong trào đấu tranh chống Mĩ</p><ul><li>Năm 1898 Mĩ gây chiến với Tây Ban Nha hất cẳng Tây Ban Nha và chiếm Philippin.</li><li>Nhân dân Philippin anh dũng chống Mĩ đến  năm 1902 thất bại. Philippin trở thành thuộc địa của Mĩ.</li></ul><img class=\"img-responsive\" src=\"public/images/noidung/bai21/Bonifacio-World-History.jpg\"></img><blockquote>Hình 21.3: Bonifacio (Phi-líp-pin)</blockquote><img class=\"img-responsive\" src=\"public/images/noidung/bai21/Jose-Rizal-Ho-xe-Ri-dan-World-History.jpg\"></img><blockquote>Hình 21.4: Jose Rizal - Hô-xê Ri-dan (Phi-líp-pin)</blockquote><h2>4.Phong trào đấu tranh chống thực dân Pháp của nhân dân Campuchia</h2><p>Bối cảnh Cam-pu-chia giữa thế kỉ XIX</p><ul><li>Trước khi bị Pháp xâm lược triều đình phong kiến Nô-rô-đôm suy yếu phải thần phục Thái Lan.</li><li>Năm 1863 Cam-pu-chia chấp nhận sự bảo hộ của Pháp. Năm 1884 Pháp gạt Xiêm, biến Cam-pu-chia thành thuộc địa của Pháp.</li><li>Ách thống trị của Pháp làm cho nhân dân Cam-pu-chia bất bình vùng dậy đấu tranh.</li></ul><p>Phong trào đấu tranh chống Pháp của nhân dân Cam-pu-chia</p><table><tr><th>Tên phong trào</th>khởi nghĩa</th><th>Thời gian</th><th>Địa bàn hoạt động</th><th>Kết quả</th></tr><tr><td>Khởi nghĩa Si-vô-tha</td><td>1861-1892</td><td>Tấn công U-đong và Phnôm Pênh</td><td>Thất bại</td></tr><tr><td>Khởi nghĩa A-cha Xoa</td><td>1863-1866</td><td>Các tỉnh giáp biên giới Việt Nam, nhân dân Châu đốc (Hà Tiên) ủng hộ A-cha-xoa chống Pháp</td><td>Thất bại</td></tr><tr><td>Khởi nghĩa Pu-côm-bô</td><td>1866-1867</td><td>Lập căn cứ ở Tây Ninh (Việt Nam) sau đó tấn công về Cam-pu-chia kiểm soát Pa-man tấn công U-đong</td><td>Thất bại</td></tr></table><p>Nổ ra liên tục, có cuộc khởi nghĩa kéo dài tới 30 năm.</p><p>Các cuộc đấu tranh thu hút được đông đảo các tầng lớp nhân dân tham gia.</p><p>Có sự ủng hộ của nhân dân Việt Nam, đặc biệt là trong cuộc khởi nghĩa của Pu-côm-bô được coi là biểu tượng về liên minh chiến đấu của nhân dân hai nước Việt Nam - Cam-pu-chia trong cuộc đấu tranh chống thực dân Pháp.</p><h2>5.Phong trào đấu tranh chống thực dân Pháp của nhân dân Lào đầu thế kỷ XX</h2><p>Bối cảnh lịch sử</p><p>Giữa thế kỉ XIX chế độ phong kiến suy yếu Lào phải thuần phục Thái Lan.</p><p>Năm  1893 bị thực dân Pháp xâm lược.</p><table><tr><th>Tên khởi nghĩa</th><th>Thời gian</th><th>Địa bàn hoạt động</th><th>Kết quả</th></tr><tr><td>Khởi nghĩa Pha-ca-đuốc</td><td>1901-1903</td><td>Xa-va-na-khet, Đường 9, Biên giới Việt - Lào</td><td>Thất bại</td></tr><tr><td>Khởi nghĩa Ong Kẹo và Com-ma-đam</td><td>1901-1937</td><td>Cao nguyên Bô-lô-ven</td><td>Thất bại</td></tr><tr><td>Khởi nghĩa Châu Pa-chay</td><td>1918-1922</td><td>Bắc Lào, Tây Bắc Việt Nam</td><td>Thất bại</td></tr></table><p>Nhận xét</p><p>Phong trào đấu tranh của nhân dân Lào, và Cam-pu-chia cuối thế kỉ XIX đầu thế kỉ XX diễn ra liên tục, sôi nổi nhưng còn mang tính tự phát.</p><p>Hình thức đấu tranh chủ yếu là khởi nghĩa vũ trang.</p><p>Lãnh đạo là các sĩ phu yêu nước và nông dân.</p><p>Kết quả</p><p>Các cuộc đấu tranh đều thất bại do tự phát thiếu đường lối đúng đắn, thiếu tổ chức vững vàng.</p><p>Thể hiện tinh thần yêu nước và tinh thần đoàn kết của nhân dân 3 nước Đông Dương.</p><h2>6.Xiêm (Thái Lan) giữa thế kỷ XIX - đầu thế kỉ XX</h2><p>Bối cảnh lịch sử</p><p>Năm 1752 triều đại Ra-ma theo đuổi chính sách đóng cửa.</p><p>Giữa thế kỉ XIX đứng trước sự đe dọa xâm lược của phương Tây, Ra-ma IV (Mông-kút ở ngôi từ 1851-1868) đã thực hiện mở cửa buôn bán với nước ngoài.</p><p>Ra-ma V (Chu-la-long-con ở ngôi từ 1868 - 1910) đã thực hiện nhiều chính sách cải cách.</p><img class=\"img-responsive\" src=\"public/images/noidung/bai21/Chu-la-long-con-Ra-ma-World-History.jpg\"></img><blockquote>Hình 21.5: Chu-la-long-con Ra-ma V</blockquote><p>Nội dung cải cách</p><p>Kinh tế</p><p>Nông nghiệp: để tăng nhanh lượng gạo xuất khẩu nhà nước giảm nhẹ thuế ruộng, xóa bỏ chế độ lao dịch.</p><p>Công thương nghiệp: khuyến khích tư nhân bỏ vốn kinh doanh, xây dựng nhà máy, mở hiệu buôn, ngân hàng</p><p>Chính trị</p><p>Cải cách theo khuôn mẫu Phương Tây.</p><p>Đứng đầu nhà nước vẫn là vua.</p><p>Giúp việc có hội đồng nhà nước (nghị viện).</p><p>Chính phủ có 12 bộ trưởng.</p><p>Quân đội, tòa án, trường học được cải cách theo khuôn mẫu phương Tây.</p><p>Về xã hội: xóa bỏ chế độ nô lệ , giải phóng người lao động.</p><p>Đối ngoại:</p><p>Thực hiện chính sách ngoại giao mềm dẻo.</p><p>Lợi dụng vị trí nước đệm.</p><p>Lợi dụng mâu thuẫn giữa 2 thế lực Anh - Pháp đã lựa chiều có lợi để giữ chủ quyền đất nước.</p><p>Tính chất</p><ul><li>Thái Lan phát triển theo hướng tư bản chủ nghĩa và giữ được chủ quyền độc lập.</li><li>Tính chất một cuộc cách mạng tư sản không triệt để.</li></ul><p>Trong bối cảnh chung của châu Á, Thái Lan đã thực hiện đường lối cải cách, chính nhờ đó mà Thái Lan thoát khỏi thân phận thuộc địa giữ được độc lập.</p></div>', 'public/images/noidung/bai21/DNA-World-History.jpg', 1, NULL, '2018-04-06 17:00:00', NULL),
(21, 9, 'Châu Phi và khu vực Mỹ Latinh', 'Chau-Phi-va-khu-vuc-My-Latinh', '<p>Nhưng cùng vào thời điểm này thì chế độ nông nô đã đi vào giai đoạn kết thúc ở châu Âu và trong đầu thế kỷ XIX thì các lực lượng thực dân châu Âu đã tiến hành sự \"tranh giành châu Phi\" vô cùng khủng khiếp và đã chiếm đóng nhiều vùng đất của châu lục này, tạo ra nhiều quốc gia thuộc địa, chỉ để sót lại 2 quốc gia độc lập là Liberia, thuộc địa của người Mỹ da đen và Ethiopia. Sự chiếm đóng này còn tiếp diễn cho đến tận sau khi kết thúc Chiến tranh thế giới thứ hai, khi các nước thuộc địa dần dần giành được quy chế độc lập hình thức.</p><p>Khu vực Mĩ Latinh là một bộ phận lãnh thổ rộng lớn của châu Mĩ, gồm một phần Bắc Mĩ, toàn bộ Trung Mĩ, Nam Mĩ và những quần đảo thuộc vùng Ca-ri-bê.Từ thế kỉ XVI, XVII, đa số các nước Mĩ Latinh lần lượt biến thành thuộc địa của Tây Ban Nha và Bồ Đào Nha. Chủ nghĩa thực dân thiết lập ở đây chế độ thống trị rất phản động, gây ra nhiều tội ác dã mạn, tàn khốc.</p>', '<div class=\"post\"><img class=\"img-responsive\" src=\"public/images/noidung/bai22/Thuc-dan-chia-nhau-World-History.jpg\"></img><blockquote>Hình 22.1: Thực dân chia nhau Châu Phi</blockquote><h2>1.Châu Phi</h2><p>Châu Phi là lục địa lớn thứ 2 trên thế giới, giàu tài nguyên khoáng sản, có nền văn hóa lâu đời.</p><p>Châu Phi có nền văn minh cổ đại rực rỡ.</p><p>Các Đế quốc xâm lược phân chia châu Phi</p><p>Từ giữa thế kỉ XIX thực dân châu Âu bắt đầu xâm lược châu Phi.</p><p>Vào những năm 70, 80 của thế kỉ XIX, sau khi hoàn thành kênh đào Xuy-ê, các nước tư bản phương Tây đua nhau xâu xé châu Phi.</p><ul><li>Anh chiếm: Nam Phi, Ai Cập, Đông Xu-đăng, một phần Đông Phi, Kênia, Xômali, Gam-bi-a.</li><li>Pháp chiếm: Tây Phi, miền xích đạo châu Phi, Ma-đa-ga-xca, một phần Xô-ma-li, An-giê-ri, Tuy-ni-di, Xa-ha-ra.</li><li>Đức: Camôrun, Tôgô, Tây Nam Phi, Tadania.</li><li>Bỉ: chiếm Công gô.</li><li>Bồ Đào Nha: Mô-dam Bích, Ănggôla và một phần Ghinê.</li></ul><p>Đầu thế kỉ XX việc phân chia thụôc địa giữa các đế quốc ở châu Phi căn bản đã hoàn thành.</p><img class=\"img-responsive\" src=\"public/images/noidung/bai22/Cong-nhan-don-dien-cao-su-o-Cong-Go-World-History.jpg\"></img><blockquote>Hình 22.2: Công nhân đồn điền cao su ở Công gô thuộc Bỉ</blockquote><p>Các cuộc đấu tranh tiêu biểu của nhân dân châu Phi</p><table> <tr><th>Thời gian</th><th>Phong trào đấu tranh</th><th>Kết quả</th></tr><tr><td>1830-1874</td><td>Cuộc đấu tranh của Áp-đen Ca-đê ở Angiêri thu hút đông đảo lực lượng tham gia</td><td>Pháp mất nhiều thập niên mới chinh phục được nước này.</td></tr><tr><td>1879-1882</td><td>Ở Ai Cập Atmet Arabi lãnh đạo phong trào “Ai Cập trẻ”</td><td>Năm 1882 các đế quốc mới ngăn chặn được phong trào</td></tr><tr><td>1882-1898</td><td>Mu-ha-met At-mét đã lãnh đạo nhân dân Xu-Đăng chống thực dân Anh</td><td>Năm 1898 phong trào bị đàn áp đẫm máu nên thất bại</td></tr><tr><td>1889</td><td>Nhân dân Ê-ti-ô-pi-a tiến hành kháng chiến chống thực dân Italia.</td><td>Ngày 01/3/1896 Italia thất bại, Êtiôpia giữ được độc lập<br>Cùng với Libêria là những nước châu Phi giữ được độc lập ở cuối thế kỉ XIX đến XX.</td></tr></table><p>Kết quả: phong trào đấu tranh chống thực dân của nhân dân châu Phi đều thất bại (trừ Êtiôpia).</p><p>Nguyên nhân thất bại là do: chênh lệch lực lượng, trình độ tổ chức thấp, bị thực dân đàn áp.</p><p>Ý nghĩa: thể hiện tinh thần yêu nước, tạo tiền đề cho giai đoạn sau - đầu thế kỉ XX.</p><p>Phong trào đấu tranh ở châu Phi bao gồm đấu tranh bảo vệ độc lập và đấu tranh chống ách đô hộ của chủ nghĩa thực dân.</p><img class=\"img-responsive\" src=\"public/images/noidung/bai22/Abd-el-Kader-World-History.jpg\"></img><blockquote>Hình 22.3: Abd el-Kader (Angieri)</blockquote><img class=\"img-responsive\" src=\"public/images/noidung/bai22/Khoi-nghia-cua-Abd-el-Kader-World-History.jpg\"></img><blockquote>Hình 22.4: Khởi nghĩa của Abd el-Kader(Angerie)</blockquote><img class=\"img-responsive\" src=\"public/images/noidung/bai22/Atmet-Arabi-World-History.jpg\"></img><blockquote>Hình 22.5: Atmet Arabi - Ai Cập</blockquote><img class=\"img-responsive\" src=\"public/images/noidung/bai22/Mu-ha-met-At-met-World-History.jpg\"></img><blockquote>Hình 22.6: Mu-ha-met At-mét (Xu đăng)</blockquote><img class=\"img-responsive\" src=\"public/images/noidung/bai22/Luoc-do-khu-vuc-My-La-tinh-World-History.jpg\"></img><blockquote>Hình 22.7: Lược đồ khu vực Mỹ La Tinh đầu thế kỷ XIX</blockquote><p>Mĩ La-tinh: là một phần lãnh thổ rộng lớn của châu Mĩ. Gồm một phần Bắc Mĩ, toàn bộ Trung Mĩ, Nam Mĩ và những quần đảo ở vùng biển Ca-ri-bê. Sở dĩ gọi đây là khu vực Mĩ La-tinh vì cư dân ở đây nói tiếng Tây Ban Nha hay Bồ Đào Nha (ngữ hệ La -tinh).</p><p>Trước khi xâm lược, Mĩ La-tinh là một khu vực có lịch sử văn hóa lâu đời, giàu tài nguyên. Cư dân bản địa ở đây là người Inđian, chủ nhân của nhiều văn hóa cổ nổi tiếng, văn hóa May-a, văn hóa In-ca, văn hóa A-dơ-tếch.</p><h2>2.Khu vực Mĩ La-tinh</h2><p>Chế độ thực dân ở Mĩ La-tinh:</p><p>Đầu thế kỉ XIX, đa số các nước Mĩ La-tinh đều là thuộc địa của Tây Ban Nha, Bồ Đào Nha.<p><p>Chủ nghĩa thực dân đã thiết lập chế độ thống trị phản động, dã man, tàn khốc:</p><ul><li>Tàn sát dồn đuổi cư dân bản địa, chiếm đất đai lập đồn điền</li><li>Đưa người Châu Phi sang để khai thác tài nguyên (vàng và bạc, người ta còn chở từ châu Mĩ về Tây Ban Nha đường, ca cao, gỗ, đá quý, ngọc trai, cánh kiến, thuốc lá, bông...)</li></ul><p>Phong trào đấu tranh giành độc lập</p><table><tr><th>Thời gian</th><th>Tên nước</th><th>Kết quả</th></tr><tr><td>Cuối XVIII</td><td>Ở Haiti bùng nổ cuộc đấu tranh (1791) chống Pháp  dưới sự lãnh đạo của Tút-xanh Lu-véc-tuy-a.</td><td>Năm 1803 thắng lợi.<br>Haiti thành nước cộng hòa da đen đầu tiên ở Nam Mĩ.<br>Cổ vũ phong trào đấu tranh ở Mĩ La-tinh.</td></tr><tr><td>20 năm đầu thế kỉ XX</td><td>Phong trào đấu tranh nổ ra sôi nổi, quyết liệt ,các quốc gia độc lập ở Mĩ La-tinh lần lượt hình thành.</td><td>Các quốc gia độc lập ra đời:<br><ul><li>Mê hi cô: 1821</li><li>Áchentina: 1816</li><li>Urugoay: 1828</li><li>Paragoay: 1811</li><li>Braxin: 1822</li><li>Pê-ru: 1821</li><li>Cômbia: 1830</li><li>Ecuađo: 1830</li></ul></td></tr></table><p>Nhận xét</p><p>Đầu thế kỉ XX phong trào đấu tranh giành độc lập ở Mĩ La-tinh diễn ra sôi nổi, quyết liệt. Kết quả hầu hết khu vực đã thóat khỏi ách thống trị của thực dân Tây Ban Nha, Bồ Đào Nha trở thành quốc gia độc lập</p><p>Tình hình Mĩ La-tinh sau khi giành độc lập và chính sách bành trướng của Mĩ</p><p>Sau khi giành độc lập, các nước Mĩ La-tinh có tiến bộ về kinh tế xã hội: Braxin trồng nhiều bông và cao su, cung cấp một nửa cà phê cho thị trường thế giới. Achentina sản xuất len, da cừu, thịt bò xuất khẩu sang Anh... Các đồn điền trồng lúa mì, cây công nghiệp, chăn nuôi lấy thịt, sữa và lông phát triển mạnh trở thành nguồn hàng xuất khẩu có giá trị của nhiều nước. Dân số tăng nhanh do người nhập cư ngày càng đông.</p><p>Mĩ âm mưu biến Mĩ La-tinh thành “sân sau” của Mĩ ở Mĩ La-tinh.</p><p>Để thực hiện được âm mưu của mình, Mĩ đã đưa ra thủ đoạn tuyên truyền học thuyết: “Châu Mĩ của người châu Mĩ” (1823), thành lập “Liên minh dân tộc các nước cộng hòa châu Mĩ”  ( Liên Mỹ )dưới sự chỉ huy của Oa-sinh-tơn.</p><p>Năm 1898  Mỹ hất cẳng Tây Ban Nha (người châu Âu) khởi châu Mĩ.</p><p>Đầu thế kỉ XX, dùng chính sách “Cái gậy lớn” và “Ngoại giao đô la” để khống chế khu vực này.</p><p>Mĩ La-tinh trở thành thuộc địa kiểu mới của Mĩ.</p><img class=\"img-responsive\" src=\"public/images/noidung/bai22/Tut-xanh-Lu-vac-tuy-a-World-History.jpg\"></img><blockquote>Hình 22.8: Tút-xanh Lu-véc-tuy-a(Ha-i-ti)</blockquote><img class=\"img-responsive\" src=\"public/images/noidung/bai22/Cuoc-khoi-nghia-cua-Tut-xanh-World-History.jpg\"></img><blockquote>Hình 22.9: Cuộc khởi nghĩa của Tút-xanh Lu-véc-tuy-a(Ha-i-ti) năm 1791</blockquote></div>', 'public/images/noidung/bai22/No-le-da-den-World-History.jpg', 1, NULL, '2018-04-06 17:00:00', NULL),
(22, 9, 'Những thành tựu văn hóa cận đại', 'Nhung-thanh-tuu-van-hoa-can-dai', '<p>Văn hóa là khái niệm mang nội hàm rộng với rất nhiều cách hiểu khác nhau, liên quan đến mọi mặt đời sống vật chất và tinh thần của con người.</p><p>Từ tương ứng với văn hóa theo ngôn ngữ của phương Tây có nguồn gốc từ các dạng của động từ Latin colere là colo, colui, cultus với hai nghĩa:  giữ gìn, chăm sóc, tạo dựng trong trồng trọt; cầu cúng. Từ văn hóa trong tiếng Việt là từ gốc Nhật, người Nhật dùng từ này để định nghĩa cách gọi văn hóa theo phương tây.</p><p>Trong cuộc sống hàng ngày, văn hóa thường được hiểu là văn học, nghệ thuật như thơ ca, mỹ thuật, sân khấu, điện ảnh... Các \"trung tâm văn hóa\" có ở khắp nơi chính là cách hiểu này. Một cách hiểu thông thường khác: văn hóa là cách sống bao gồm phong cách ẩm thực, trang phục, cư xử và cả đức tin, tri thức được tiếp nhận... Vì thế chúng ta nói một người nào đó là văn hóa cao, có văn hóa hoặc văn hóa thấp, vô văn hóa.</p>', '<div class=\"post\"><h2>1.Sự phát triển của nền văn hóa mới trong buổi đầu thời cận đại đến giữa thế kỉ XIX.</h2><p>Sau các cuộc cách mạng tư sản và cách mạng công nghiệp, kinh tế các nước có điều kiện phát triển.</p><p>Trong xã hội tồn tại những mối quan hệ cũ, mới chồng chéo phức tạp, đây là hiện thực  để các nhà văn, thơ, kịch có điều kiện sáng tác.</p><p>Thành trì của chế độ phong kiến lung lay.</p><img class=\"img-responsive\" src=\"public/images/noidung/bai23/Ho-thien-nga-World-History.jpg\"></img><blockquote>Hình 23.1: Hồ thiên nga của Trai-cốp-ki-Nga</blockquote><p>Văn học</p><p>Ở Phương Tây</p><p>Xuất hiện những nhà tư tưởng tiến bộ, những nhà văn, thơ, nhà viết kịch nổi tiếng.</p><p>Cooc-nây (1606 - 1684) bi kịch cổ điển Pháp.</p><p>La Phông-ten (1621 - 1695) là nhà thơ ngụ ngôn Pháp.</p><p>Mô-li-e (1622 - 1673) là người mở đầu cho nền hài kịch cổ điển Pháp...</p><p>Ban-dắc (Pháp 1799 - 1850).</p><p>An-đéc-xen (Đan Mạch, 1805 - 1875).</p><p>Pu-skin (Nga, 1799 - 1837).</p><img class=\"img-responsive\" src=\"public/images/noidung/bai23/Truyen-ngu-ngon-La-Phong-ten-World-History.jpg\"></img><blockquote>Hình 23.2: Trang bìa của truyện Ngụ ngôn của La Phông-ten</blockquote><p>Châu Á</p><p>Tào Tuyết Cần (1716 - 1763) của Trung Quốc; Nhật Bản có nhà thơ, nhà soạn kịch xuất sắc Chi-ka-mát-xư Môn-đa-ê-môn (1653 - 1725); ở Việt Nam thế kỉ XVIII có nhà bác học Lê Quý Đôn (1726 - 1784),...</p><p>Phản ánh hiện thực xã hội ở các nước trên thế giới thời kỳ cận đại.</p><p>Hình thành quan điểm, tư tưởng của con người tư sản, tấn công vào thành trì của chế độ phong kiến, góp phần vào thắng lợi của chủ nghĩa tư bản.</p><p>Âm nhạc</p><p>Bét tô ven - Đức - sáng tác thấm đượm tinh thần  dân chủ cách mạng.</p><p>Mô da (1756-1791)- người Áo.</p><p>Hội họa</p><p>Rem-bran (1606-1669) - hà Lan, vẽ chân dung, phong cảnh.</p><p>Những thành tựu về mặt tư tưởng, văn hóa đến thế kỉ XIX</p><p>Trào lưu Triết học Ánh sáng thế kỉ XVII - XVIII ở Pháp, tiêu biểu như: Mông-te-xki-ơ (1689 - 1755), Vôn-te (1694 - 1778), G. Rút-tô (1712 - 1778)</p><p>Nhóm Bách khoa toàn thư do Đi-đơ-rô đứng đầu. Được ví “Như những khẩu đại bác, mở đường cho bộ binh xuất kích”</p><h2>2.Thành tựu của văn học, nghệ thuật từ giữa thế kỉ XIX đến đầu thế kỉ XX</h2><h4>a.Điều kiện lịch sử</h4><p>Chủ nghĩa tư bản được xác lập trên phạm vi toàn thế giới và bước sang giai đoạn chủ nghĩa đế quốc.</p><p>Giai cấp tư sản nắm quyền thống trị, mở rộng và xâm lược thuộc địa thì đời sống nhân dân lao động bị áp bức ngày càng khốn khổ.</p><h4>b.Thành tựu tiêu biểu về văn học, nghệ thuật đầu thế kỉ XIX đến đầu thế kỉ XX</h4><h5>Văn học</h5><p>Ở phương Tây</p><p>Vích to Huy-gô (1802 - 1885):Những người khốn khổ, thể hiện lòng yêu thương vô hạn  đối với những người đau khổ, mong tìm hạnh phúc cho họ.</p><p>Lép Tôn-xtôi (1828 - 1910): Chiến tranh và hòa bình, An-na Ka rê ni na, Phục sinh... chống lại phong kiến Nga Hoàng.</p><p>Mác-Tuên (1935 - 1910): Những cuộc phiêu lưu của Tôm Xoay ơ, trào phúng, hài hước, mang tinh thần phê phán sâu sắc.</p><p>Pu-skin - Nga; Ban dắc - Pháp.....</p><img class=\"img-responsive\" src=\"public/images/noidung/bai23/Nhung-nguoi-khon-kho-World-History.jpg\"></img><blockquote>Hình 23.3: Những người khốn khổ của Vích-to Huy-go</blockquote><p>Ở phương Đông: phản ánh cuộc sống của nhân dân dưới ách thực dân phong kiến, lòng khát khao và ý chí anh hùng quật khởi  trong đấu tranh cho độc lập tự do.</p><p>Lỗ Tấn (1881 - 1936): A.Q. Chính chuyện; Nhật kí người điên, Thuốc,...</p><p>Ra-bin-đra-nát Ta-go - Ấn Độ, thơ, kịch, tiểu thuyết, truyện ngắn...tập Thơ Dâng...thể hiện lòng yêu nước, yêu hoà bình và tình nhân đạo...</p><p>ô-xê Mác-ti (1823 - 1893): nhà thơ nổi tiếng của Cu ba.</p><h5>Nghệ thuật</h5><p>Kiến trúc: Cung điện Véc xai được hoàn thành vào năm 1708; Bảo tàng Anh; Viện bảo tàng Ec-mi-ta-giơ; Bảo tàng Lu-vrơ (Pa-ri-Pháp), là bảo tàng bằng hiện vật lớn nhất thế giới.</p><p>Hội hoạ: họa sĩ Van Gốc (Hà Lan) với tác phẩm Hoa hướng dương, Phu-gita (Nhật Bản), Pi-cát-xô (Tây Ban Nha)... Lê-vi-tan (Nga).</p><p>Âm nhạc: Trai-cốp-ki với Hồ thiên nga, Người đẹp ngủ trong rừng.</p><p>Tác dụng: phản ánh hiện thực xã hội, mong ước xây dựng một xã hội mới tốt đẹp hơn.</p><img class=\"img-responsive\" src=\"public/images/noidung/bai23/Cung-dien-Vec-xai-World-History.jpg\"></img><blockquote>Hình 23.4: Cung điện Véc-xai</blockquote><img class=\"img-responsive\" src=\"public/images/noidung/bai23/Tac-pham-thang-ba-World-History.jpg\"></img><blockquote>Hình 23.5: Tác phẩm tháng ba của Le-vi-tan - Nga</blockquote><img class=\"img-responsive\" src=\"public/images/noidung/bai23/Mua-thu-vang-World-History.jpg\"></img><blockquote>Hình 23.6: Mùa thu vàng - Levitan</blockquote><h2>3.Trào lưu tư tưởng tiến bộ và sự ra đời của  chủ  nghĩa xã hội khoa học từ giữa thế kỷ XIX đến đầu thế kỷ XX</h2><p>Chủ nghĩa xã hội không tưởng</p><p>Những nhà tư tưởng tiến bộ Xanh Xi-mông, Phu-ri-ê, Ô-oen: mong muốn xây dựng một xã hội không có chế độ tư hữu, không có áp bức bóc lột... Không tưởng vì tư tưởng của họ không thực hiện được trong điều kiện chủ nghĩa tư bản vẫn được duy trì và phát triển.</p><p>Triết học Đức và kinh tế chính trị học Anh:</p><ul><li>Hê-ghen (1770 - 1831) và Phoi-ơ-bách (1804 - 1872) nhà triết học nổi tiếng người Đức. Hê-ghen là nhà duy tâm khách quan còn Phoi-ơ-bách là nhà duy vật siêu hình...</li><li>Khoa Kinh tế - chính trị cổ điển phát sinh ở Anh Ađam Xmít (1723 - 1790) và Ri-các-đô (1772 - 1823),“lí luận về  giá trị lao động”,nhưng chỉ mới nhìn thấy mối quan hệ giữa vật và vật chứ chưa thấy mối quan hệ giữa người với người.</li></ul><p>Chủ nghĩa xã hội khoa học</p><p>Hoàn cảnh</p><ul><li>Sự áp bức bóc lột của giai cấp tư sản giai đoạn chủ nghĩa đế quốc.</li><li>Phong trào công nhân phát triển, cùng với học thuyết Chủ nghĩa xã hội khoa học  ra đời do C.Mác và Ph. Ăngghen thành lập, được Lê-nin phát triển.</li></ul><p>Nội dung</p><ul><li>Kế thừa, phát triển có chọn lọc những thành tựu của khoa học xã hội và tự nhiên mà loài người đã đạt được, chủ yếu từ thế kỉ XIX (định luật bảo toàn và chuyển hóa năng lượng, học thuyết tế bào,  thuyết tiến hóa của các giống loài, các trào lưu triết học cổ điển Đức, học thuyết kinh tế Anh và lý luận về chủ nghĩa xã hội  không tưởng ở Pháp).</li><li>Học thuyết gồm bà bộ phận chính: triết học, kinh tế - chính trị học và chủ nghĩa xã hội khoa học (Chủ nghĩa duy vật biện chứng và Chủ nghĩa duy vật lịch sử kết hợp chặt chẽ với nhau).</li></ul><p>Điểm khác: Xây dựng học thuyết của mình trên quan điểm, lập trường giai cấp công nhân, thực tiễn đấu tranh của phong trào cách mạng vô sản thế giới,hình thành hệ thống lý luận vừa mới khoa học vừa cách mạng.</p><p>Vai trò</p><p>Chủ nghĩa Mác - Lê-nin:</p><ul><li>Là đỉnh cao của trí tuệ loài người, là cương lĩnh cách mạng cho cuộc đấu tranh chống chủ nghĩa tư bản, xây dựng xã hội cộng sản.</li><li>Và mở ra kỷ nguyên mới cho sự phát triển của khoa học (tự nhiên và xã hội, nhân văn).</li></ul></div>', 'public/images/noidung/bai23/Van-hoa-can-dai-World-History.jpg', 1, NULL, '2018-04-06 17:00:00', NULL),
(23, 10, 'Chiến tranh thế giới thứ nhất (1914-1918)', 'Chien-tranh-the-gioi-thu-nhat(1914-1918)', '<p>Chiến tranh thế giới thứ nhất, còn được gọi là Đại chiến thế giới lần thứ nhất, Đệ Nhất thế chiến hay Thế chiến 1, diễn ra từ 28 tháng 7 năm 1914 đến 11 tháng 11 năm 1918, là một trong những cuộc chiến tranh quyết liệt, quy mô to lớn nhất trong lịch sử nhân loại; về quy mô và sự khốc liệt nó chỉ đứng sau Chiến tranh thế giới thứ hai.</p><p>Cuộc chiến tranh này là một trong những sự kiện lịch sử có ảnh hưởng lớn nhất trong lịch sử thế giới.[1] Đây là cuộc chiến tranh có chiến trường chính bao trùm khắp châu Âu và ảnh hưởng ra toàn thế giới, lôi kéo tất cả các cường quốc châu Âu và Bắc Mỹ vào vòng chiến với số người chết trên 19 triệu người với sức tàn phá và ảnh hưởng về vật chất tinh thần cho nhân loại rất sâu sắc và lâu dài....</p>', '<div class=\"post\"><h2>1.NGUYÊN NHÂN CỦA CHIẾN TRANH</h2><h4>a. Quan hệ quốc tế cuối thế kỷ XIX - đầu thế kỷ XX</h4><p>Chủ nghĩa tư bản phát triển theo quy luật không đều làm thay đổi sâu sắc so sánh lực lượng giữa các đế quốc ở cuối XIX đầu XX.</p><p>Sự phân chia thuộc địa giữa các đế quốc cũng không đều. Đế quốc già (Anh, Pháp) nhiều thuộc địa. Đế quốc trẻ (Đức, Mĩ) ít thuộc địa.</p><p>Mâu thuẫn giữa các đế quốc về vấn đề thuộc địa nảy sinh và ngày càng gay gắt.</p><p>Các cuộc chiến tranh giành thuộc địa đã nổ ra ở nhiều nơi vào cuối thế kỷ XIX:</p><table><tr><th>Thời gian</th><th>Chiến tranh</th><th>Kết quả</th></tr><tr><td>1894- 1895</td>\r\n<td>Chiến tranh Trung-Nhật</td><td>Nhật chiếm Đài Loan, Triều Tiên, Mãn Châu, Bành Hồ</td></tr><tr><td>1898</td><td>Chiến tranh Mĩ-Tây Ban Nha</td><td>Mĩ cướp được Phi-lip-pin, Cu-ba, Ha-oai, Guy-a-na, Pu-éc-tô Ri-cô</td></tr><tr><td>1899-1902</td><td>Chiến tranh Anh - Bô-ơ</td><td>Anh chiếm Nam Phi</td></tr><tr><td>1904-1905</td><td>Chiến tranh Nga-Nhật</td><td>Nhật thống trị Triều Tiên, Mãn Châu và một số đảo ở nam Xa-kha-lin</td></tr></table><img class=\"img-responsive\" src=\"public/images/noidung/bai24/Luoc-do-chu-nghia-tu-ban-World-History.jpg\"></img><blockquote>Hình 24.1: Lược đồ Chủ nghĩa tư bản (thế kỷ XVI đến 1914)</blockquote><p>Trong cuộc chạy đua giành giật thuộc địa, Đức là kẻ hiếu chiến nhất, lại ít thuộc địa . Đức đã cùng Áo - Hung, Italia thành lập “phe Liên Minh”, năm 1882 chuẩn bị chiến tranh chia lại thế giới.</p><p>Để đối phó Anh đã ký với Nga và Pháp những Hiệp ước tay đôi hình thành phe Hiệp ước (đầu thế kỉ XX).</p><p>Đầu thế kỉ XX ở châu Âu đã hình thành 2 khối quân sự đối đầu nhau, âm mưu xâm lược, cướp đoạt lãnh thổ và thuộc địa của nhau, điên cuồng chạy đua vũ trang, chuẩn bị cho chiến tranh, một cuộc chiến tranh đế quốc nhằm phân chia thị trường thế giới không thể tránh khỏi.</p><h5>Liên minh                                    Hiệp ước</h5><h5>ĐỨC - ÁO - HUNG        <-->             ANH - PHÁP - NGA</h5><h5>(1882)                                     (1890-1907)</h5><p>Hai khối quân sự ráo riết chạy đua vũ trang  tích cực chuẩn bị chiến tranh</p><img class=\"img-responsive\" src=\"public/images/noidung/bai24/Khoi-Lien-minh-va-khoi-Hiep-uoc-World-History.jpg\"></img><blockquote>Hình 24.2: Hai khối: màu đỏ khối Liên Minh,màu xanh khối Hiệp ước</blockquote><h4>b.Nguyên nhân dẫn đến chiến tranh</h4><p>Nguyên nhân sâu xa:</p><ul><li>Sự phát triển không đều của các nước đế quốc ,mâu thuẫn giữa các đế quốc  về thuộc địa ngày càng gay gắt( trước tiên là giữa đế quốc Anh với đế quốc Đức) là nguyên nhân cơ bản dẫn đến chiến tranh.</li><li>Sự tranh giành thị trường thuộc địa  giữa  các đế quốc với nhau.</li></ul><p>Nguyên nhân trực tiếp</p><ul><li>Sự hình thành hai khối quân sự đối lập, kình địch nhau.</li><li>Duyên cớ: 28/6/1914 Hoàng thân thừa kế ngôi vua Áo-Hung bị ám sát tại Bô-xni-a (Xéc bi)</li></ul><p>Đến năm 1914, sự chuẩn bị chiến tranh của 2 phe đế quốc cơ bản đã xong. Ngày 28.6.1914, Áo - Hung tổ chức tập trận ở Bô-xni-a. Thái tử Áo là Phơ-ran-xo Phéc-đi-nan đến thủ đô Bô-xni-a là Xa-ra-e-vô để tham quan cuộc tập trận thì bị một phần tử người Xéc-bi ám sát. Nhân cơ hội đó Đức hùng hổ bắt Áo phải tuyên chiến với Xéc-bi. Thế là chiến tranh đã được châm ngòi.</p><img class=\"img-responsive\" src=\"public/images/noidung/bai24/Francois-Ferdinand-bi-am-sat-World-History.jpg\"></img><blockquote>Hình 24.3: Francois Ferdinand bị ám sát.</blockquote><img class=\"img-responsive\" src=\"public/images/noidung/bai24/Luoc-do-chien-tranh-the-gioi-thu-nhat-World-History.jpg\"></img><blockquote>Hình 24.4: Lược đồ Chiến tranh thế giới thứ nhất</blockquote><h2>2.DIỄN BIẾN CỦA CHIẾN TRANH THẾ GIỚI LẦN THỨ NHẤT (1914-1918)</h2><h4>a.Giai đoạn thứ nhất (1914 - 1916)</h4><p>Chiến tranh bùng nổ</p><p>28/6/1914, Hoàng thân thừa kế ngôi vua Áo-Hung bị ám sát</p><p>28/7/1914, Áo-Hung tuyên chiến với Xéc-bi.</p><p>1/8/1914, Đức tuyên chiến với Nga.</p><p>3/8/1914, Đức tuyên chiến với Pháp.</p><p>4/8/1914, Anh tuyên chiến với Đức.</p><p>Chiến tranh thế giới bùng nổ diễn ra trên 2 mặt trận Đông Âu và Tây Âu.</p><img class=\"img-responsive\" src=\"public/images/noidung/bai24/Quan-Duc-vao-Phap-World-History.jpg\"></img><blockquote>Hình 24.5: Quân Đức vào Pháp</blockquote><table><tr><th>Thời gian</th><th>Chiến sự</th><th>Kết quả</th></tr><tr><td>1914</td><td>Ở phía Tây : ngay đêm 3.8 Đức tràn vào Bỉ, đánh sang Pháp.<br>Cùng lúc ở phía Đông; Nga tấn công Đông Phổ.</td><td>Đức chiếm được Bỉ, một phần nước Pháp uy hiếp thủ đô Pa-ri.<br>Cứu nguy cho Pa-ri.</td></tr><tr><td>1915</td><td>Đức, Áo - Hung dồn toàn lực tấn công Nga.</td><td>Hai bên ở vào thế cầm cự trên một Mặt trận dài 1200 km.</td></tr><tr><td>1916</td><td>Đức chuyển mục tiêu về phía Tây tấn công pháo đài Véc-doong.</td><td>Đức không hạ được Véc-đoong, 2 bên thiệt hại nặng.</td></tr></table><p>Những năm đầu Đức, Áo - Hung giữ thế chủ động tấn công. Từ cuối 1916 trở đi. Đức, Áo - Hung chuyển sang thế phòng ngự ở cả hai mặt trận Đông Âu, Tây Âu.</p><h4>b.Giai đoạn thứ 2 (1917 - 1918)</h4><table><tr><th>Thời gian</th><th>Chiến sự</th><th>Kết quả</th></tr><tr><td>2/1917</td><td>Cách mạng dân chủ tư sản ở Nga thành công.</td><td>Chính phủ tư sản lâm thời ở Nga vẫn tiếp tục chiến tranh.</td></tr><tr><td>2/4/1917</td><td>Mĩ tuyên chiến với Đức, tham gia vào chiến tranh cùng phe Hiệp ước.</td><td>Có lợi hơn cho phe Hiệp ước.</td></tr> <tr><td></td><td>Trong năm 1917 chiến sự diễn ra trên cả 2 Mặt trận Đông và Tây Âu.</td><td>Hai bên ở vào thế cầm cự.</td></tr><tr><td>11/1917</td><td>Cách mạng tháng 10 Nga thành công</td><td>Chính phủ Xô viết thành lập</td></tr><tr><td>3/3/1918</td><td>Chính phủ Xô viết ký với Đức Hiệp ước Bơ-rét Li-tốp</td><td>Nga rút khỏi chiến tranh</td></tr><tr><td>Đầu 1918</td><td>Đức tiếp tục tấn công Pháp</td><td>Một lần nữa Pa-ri bị uy hiếp</td></tr><tr><td>7/1918</td><td>Mĩ đổ bộ vào châu Âu, chớp thời cơ Anh - Pháp phản công.</td><td>Đồng minh của Đức đầu hàng: Bungari 29/9, Thổ Nhĩ Kỳ 30/10, Áo - Hung 2/11</td></tr><tr><td>9/11/1918</td><td>Cách mạng Đức bùng nổ</td><td>Nền quân chủ bị lật đổ</td></tr><tr><td>1/11/1918</td><td>Chính phủ Đức đầu hàng</td><td>Chiến tranh kết thúc</td></tr></table><img class=\"img-responsive\" src=\"public/images/noidung/bai24/My-tham-chien-cung-phe-Hiep-uoc-World-History.jpg\"></img><blockquote>Hình 24.6: Mỹ tham chiến cùng phe Hiệp ước</blockquote><img class=\"img-responsive\" src=\"public/images/noidung/bai24/Cach-mang-thang-10-Nga-World-History.jpg\"></img><blockquote>Hình 24.7: Cách mạng tháng 10 Nga thành công</blockquote><img class=\"img-responsive\" src=\"public/images/noidung/bai24/Nga-rut-khoi-cuoc-chien-World-History.jpg\"></img><blockquote>Hình 24.8: Nga rút khỏi cuộc chiến</blockquote><img class=\"img-responsive\" src=\"public/images/noidung/bai24/Cach-mang-Duc-bung-no-World-History.jpg\"></img><blockquote>Hình 24.9: Cách mạng Đức bùng nổ 9/11/1918</blockquote><h4>c.Kết cục của cuộc Chiến tranh thế giới thứ nhất:</h4><p>Hậu quả của chiến tranh</p><p>Chiến tranh thế giới thứ nhất kết thúc với sự thất bại của phe Liên Minh, gây nên thiệt hại nặng nề về người và của.</p><ul><li>10 triệu người chết.</li><li>20 triệu người bị thương.</li><li>Chiến  phí 85 tỉ đô la.</li></ul><p>Các nước Châu Âu là con nợ của Mỹ.</p><p>Bản đồ thế giới thay đổi.</p><p>Cách mạng tháng Mười Nga thành công đánh dấu bước chuyển lớn trong cục diện thế giới.</p><p>Tính chất: Chiến tranh thế giới thứ nhất là cuộc chiến tranh đế quốc phi nghĩa.</p><img class=\"img-responsive\" src=\"public/images/noidung/bai24/Chau-au-1914-World-History.jpg\"></img><blockquote>Hình 24.10: Châu âu năm 1914</blockquote><img class=\"img-responsive\" src=\"public/images/noidung/bai24/Chau-Au-sau-chien-tranh-World-History.jpg\"></img><blockquote>Hình 24.11: Châu Âu sau chiến tranh thế giới thứ nhất.</blockquote></div>', 'public/images/noidung/bai24/Chien-tranh-the-gioi-thu-nhat-World-History.jpg', 23, 'public/video/Chien-tranh-the-gioi-thu-nhat-World-History.mp4', '2018-04-06 17:00:00', NULL),
(24, 11, 'Cách mạng tháng Mười Nga và cuộc đấu tranh bảo vệ cách mạng', 'Cach-mang-thang-Muoi-Nga-va-cuoc-dau-tranh-bao-ve-cach-mang', '<p>Cách mạng Tháng Mười Nga năm 1917 là một sự kiện lịch sử đánh dấu sự ra đời của nhà nước Nga Xô viết. Cách mạng Tháng Mười nổ ra vào ngày 24 tháng 10 năm 1917 (theo lịch Julius), do Vladimir Ilyich Lenin và Ðảng Bolshevik lãnh đạo. Cách mạng Tháng Mười thành công vào ngày 7 tháng 11 năm 1917 (theo lịch Gregory) hay ngày 25 tháng 10 năm 1917 (theo lịch Julius).</p><p>Sau Cách mạng Tháng Hai, nước Nga vẫn còn tồn tại song song hai chính quyền: chính phủ lâm thời của giai cấp tư sản và xô viết đại biểu công nhân và binh lính. Sau khi nắm được chính quyền, chính phủ lâm thời đã không giải quyết những vấn đề đã hứa trước đó như vấn đề ruộng đất của nông dân, việc làm cho công nhân, tình trạng thiếu lương thực và nhất là quyết theo đuổi chiến tranh đế quốc đến cùng.</p>', '<div class=\"post\"><h2>1.Cách mạng tháng Mười Nga năm 1917</h2><h4>a.Tình hình nước Nga trước cách mạng</h4><p>Về chính trị:</p><p>Đầu thế kỉ XX Nga vẫn là một nước quân chủ chuyên chế, đứng đầu là Nga hoàng.</p><p>Nga hoàng đã đẩy nhân dân Nga vào cuộc chiến tranh đế quốc, gây nên hậu quả kinh tế xã hội nghiêm trọng.</p><p>Về kinh tế: lạc hậu, kiệt quệ vì chiến tranh, nạn đói xảy ra ở nhiều nơi, công nghiệp, nông nghiệp đình đốn.</p><p>Về xã hội:</p><p>Đời sống của nông dân, công nhân, các dân tộc trong đế quốc Nga vô cùng cực khổ.</p><p>Phong trào phản đối chiến tranh đòi lật đổ Nga hoàng diễn ra khắp nơi.</p><img class=\"img-responsive\" src=\"public/images/noidung/bai25/Tinh-canh-nguoi-nong-dan-Nga-dau-the-ki-XX-World-History.jpg\"></img><blockquote>Hình 25.1: Tình cảnh người nông dân Nga đầu thế kỉ XX.</blockquote><p>=> Chính phủ Nga hoàng bất lực, nước Nga tiến sát tới một cuộc cách mạng.</p><h4>b.Từ Cách mạng tháng Hai đến Cách mạng tháng Mười</h4><h5>Cách mạng dân chủ tư sản tháng 2/1917</h5><p>Ngày 23/2/1917 cách mạng bùng nổ bằng cuộc biểu tình của 9 vạn nữ công nhân  ở Pê-tơ-rô-gơ-rát.</p><p>Phong trào nhanh chóng chuyển từ tổng bãi công chính trị sang khởi nghĩa vũ trang.</p><p>Lãnh đạo là Đảng Bôn-sê-vích.</p><p>Lực lượng tham gia là công nhân, binh lính, nông dân.</p><p>Kết quả:</p><ul><li>Chế độ quân chủ chuyên chế Nga hoàng bị lật đổ.</li><li>Xô viết đại biểu công nhân và binh lính được thành lập (tháng 3/1917 toàn nước Nga có 555 Xô Viết)</li><p>Xô viết đại biểu công nhân và binh lính ở Pê-tơ-rô grát</p><ul><li>Cùng thời gian giai cấp tư sản cũng thành lập Chính phủ lâm thời.</li><li>Nga trở thành nước Cộng Hoà</li></ul><p>Tính chất: Cách mạng tháng Hai năm 1917 ở Nga là cuộc cách mạng dân chủ tư sản kiểu mới.</p><p>Sau Cách mạng tháng Hai, Nga tồn tại 2 chính quyền song song:</p><ul><li>Chính phủ lâm thời (tư sản).</li><li>Xô viết đại biểu (vô sản).</li></ul><p>b.Cách mạng tháng Mười Nga 1917</p><p>Hoàn cảnh: Cách mạng dân chủ tư sản tháng Hai năm 1917 mặc dù đã lật đổ chế độ Nga hoàng, song nước Nga lại tồn tại hai chính quyền song song (các Xô viết gồm đại biểu công nhân, nông dân, binh lính và chính phủ lâm thời của giai cấp tư sản và đại địa chủ tư sản hóa).</p><p>Hơn nữa, khi đó Chỉnh phủ lâm thời vẫn theo đuổi cuộc chiến tranh đế quốc, bất chấp sự phản đối mạnh mẽ của quần chúng nhân dân.</p><p>=> Đảng Bôn-sê-vích quyết tâm dùng bạo lực lật đổ Chính phủ lâm thời, chấm dứt tình trạng hai chính quyền song song tồn tại.</p><p>Diễn biến khởi nghĩa</p><p>Tháng 4: Lê-nin đã thông qua Đảng  Bôn-sê-vích bản Luận cương tháng 4 chỉ ra mục tiêu đường lối tiếp theo của cách mạng Nga là chuyển từ cách mạng dân chủ tư sản sang cách mạng xã hội chủ nghĩa.</p><img class=\"img-responsive\" src=\"public/images/noidung/bai25/Luan-cuong-Le-nin-World-History.jpg\"></img><blockquote>Hình 25.2: Luận cương tháng -1917 của Lê-nin.</blockquote><p>Đêm 24/10/1917 bắt đầu khởi nghĩa.</p><p>Đêm 25/10 tấn công cung điện Mùa Đông, bắt giữ các bộ trưởng của Chính phủ tư sản.</p><img class=\"img-responsive\" src=\"public/images/noidung/bai25/Hong-quan-Lien-Xo-World-History.jpg\"></img><blockquote>Hình 25.3: Hồng quân tiến vào chiếm Cung điện Mùa Đông.</blockquote><p>Ngày 25/10/1917 (7/11/1917) trở thành ngày thắng lợi của Cách mạng xã hội chủ nghĩa tháng Mười</p><p>Ngày 3/11/1918 chính quyền Xô viết giành thắng lợi trên khắp nước Nga rộng lớn.</p><p>Tính chất: Cách mạng tháng Mười mang tính chất của cuộc cách mạng xã hội chủ nghĩa.</p><h2>2.Các cuộc đấu tranh xây dựng và bảo vệ chính quyền Xô viết</h2><h4>a.Xây dựng chính quyền Xô viết</h4><p>Đêm 25/10/1917 chính quyền Xô viết được thành lập do Lê-nin đứng đầu.</p><p>Chính sách của chính quyền:</p><ul><li>Đập tan bộ máy Nhà nước cũ, xây dựng bộ máy Nhà nước mới.</li><li>Chính quyền Xô viết thông qua: \"Sắc lệnh hòa bình” và “Sắc lệnh ruộng đất”. Trong đó sắc lệnh hòa bình lên án cuộc chiến tranh đế quốc chủ nghĩa . Sắc lệnh ruộng đất nhằm giải quyết ruộng đất cho nông dân.</li><li>Xoá bỏ đẳng cấp, và đặc quyền của giáo hội.</li><li>Thực hiện nam nữ bình quyền, các dân tộc có quyền bình đẳng và quyền tự quyết.</li><li>Thành lập Hồng quân để bảo vệ chính quyền cách mạng.</li></ul><li>Quốc hữu hóa các nhà máy xí nghiệp của giai cấp tư sản, thành lập Hội đồng kinh tế quốc dân tối cao để xây dựng nền kinh tế xã hội chủ nghĩa.</li></ul><p>=>Chính quyền Xô viết đã đem lại lợi ích và bảo vệ lợi ích cho nhân dân lao động, thể hiện tính ưu việt tiến bộ của một chính quyền mới, chính quyền của dân, do dân, vì dân,  khác hẳn và đối lập với những chính quyền cũ của giai cấp phong kiến, tư sản ở nước Nga cũng như các nước khác ở châu Âu.</p><img class=\"img-responsive\" src=\"public/images/noidung/bai25/Dai-hoi-Xo-Viet-lan-hai-World-History.jpg\"></img><blockquote>Hình 25.4: Đại hội Xô viết toàn Nga lần thứ hai đêm 25-10-1917.</blockquote><h4>b.Bảo vệ chính quyền Xô viết.</h4><p>Cuối năm 1918 quân đội 14 nước đế quốc cấu kết với các lực lượng phản cách mạng trong nước mở cuộc tấn công nhằm tiêu diệt nước Nga Xô viết.</p><p>Để chống thù trong giặc ngoài đầu năm 1919 chính quyền Xô viết đã thực hiện chính sách “Cộng sản thời chiến”.<p><p>Nội dung của chính sách:</p><ul><li>Nhà nước kiểm soát toàn bộ nền công nghiệp: quốc hữu hóa đại công nghiệp và công nghiệp vừa và nhỏ nhằm tích lũy hàng tiêu dùng tiếp tế cho quân đội.</li><li>Trưng thu lương thực thừa của nông dân. Nhà nước độc quyền lúa mì. Năm 1920 chế độ này được áp dụng với cả khoai tây, rau đậu và nhiều nông phẩm khác.</li><li>Thi hành chế độ cưỡng bức lao động.</li></ul><p>=>Chính sách \"Cộng sản thời chiến\" đã động viên tối đa nguồn của cải nhân lực của đất nước, tạo nên sức mạnh tổng hợp, đến cuối năm 1920, Nga đẩy lùi sự can thiệp của các nước đế quốc, bảo vệ chính quyền non trẻ.</p><img class=\"img-responsive\" src=\"public/images/noidung/bai25/Luoc-do-nuoc-Nga-chong-thu-trong-giac-ngoai-World-History.png\"></img><blockquote>Hình 25.5: Lược đồ nước Nga chống thù trong giặc ngoài.</blockquote><h2>3.Ý nghĩa lịch sử Cách mạng tháng Mười Nga</h2><p>Với nước Nga</p><p>Đập tan ách áp bức, bóc lột của phong kiến, tư sản, giải phóng công nhân và nhân dân lao động.</p><p>Đưa công nhân và nông dân lên nắm chính quyền, xây dựng chủ nghĩa xã hội.</p><p>Với thế giới</p><p>Làm thay đổi cục diện thế giới.</p><p>Cổ vũ và để lại nhiều bài học kinh nghiệm cho cách mạng thế giới.</p></div>', 'public/images/noidung/bai25/Cach-mang-thang-10-Nga-World-History.jpg', 2, NULL, '2018-04-06 17:00:00', NULL),
(25, 11, 'Liên Xô xây dựng chủ nghĩa xã hội (1921-1941)', 'Lien-Xo-xay-dung-chu-nghia-xa-hoi(1921-1941)', '<p>Liên Xô, tên đầy đủ là Liên bang các nước Cộng hòa Xã hội Chủ nghĩa Xô viết  là một cựu quốc gia có lãnh thổ chiếm phần lớn châu Âu và châu Á, tồn tại từ 30 tháng 12 năm 1922 cho đến khi chính thức giải thể vào ngày 25 tháng 12 năm 1991.</p><p>Sự thành lập quốc gia này gắn liền với quá trình sụp đổ của Đế quốc Nga trong Chiến tranh thế giới thứ nhất: cuộc Cách mạng tháng Hai lật đổ chính quyền Sa hoàng và Cách mạng Tháng Mười năm 1917 lật đổ Chính phủ Lâm thời của Aleksandr Fyodorovich Kerensky sau đó. Liên Xô hình thành với chiến thắng của những người cộng sản Nga (Bolshevik) đứng đầu là Vladimir Ilyich Lenin trong cuộc cách mạng Tháng Mười và trong cuộc nội chiến Nga (1918 – 1922).</p>', '<div class=\"post\"><h2>1.Chính sách kinh tế mới và công cuộc khôi phục kinh tế (1921 - 1925)</h2><h4>a.Chính sách kinh tế mới</h4><p>Hoàn cảnh lịch sử</p><p>Sau 7 năm chiến tranh liên miên, nền kinh tế quốc dân bị tàn phá nghiêm trọng.</p><p>Tình hình chính trị không ổn định. Các lực lượng phản cách mạng điên cuồng chống phá gây bạo loạn ở nhiều nơi.</p><p>Chính sách cộng sản thời chiến đã lạc hậu kìm hãm nền kinh tế, khiến nhân dân bất bình. Nước Nga Xô viết lâm vào khủng hoảng.</p><p>=>Tháng 3/1921 Đảng Bôn-sê-vích quyết định thực hiện Chính sách Kinh tế mới (NEP) do Lê-nin đề xướng.</p><p>Nội dung</p><p>Nông nghiệp: thay thế chế độ trưng thu lương thực thừa  bằng thu thuế lương thực, (ban hành thuế nông nghiệp).</p><p>Công nghiệp:</p><ul><li>Nhà nước khôi phục công nghiệp nặng.</li><li>Tư nhân  xây dựng những xí nghiệp nhỏ,dưới 20 công nhân.</li><li>Khuyến khích nước ngoài đầu tư vào nước Nga.</li><li>Nhà nước nắm các ngành kinh tế chủ chốt.</li></ul><p>Thực chất là chuyển nền kinh tế do nhà nước độc quyền sang nền kinh tế hàng hóa nhiều thành phần do nhà nước kiểm soát, khôi phục lại nền kinh tế hàng hóa...</p><p>Tác dụng - ý nghĩa</p><p>Thúc đẩy kinh tế quốc dân chuyển biến rõ rệt, giúp nhân dân Xô viết vượt qua khó khăn, hoàn thành khôi phục kinh tế.</p><p>Là bài học đối với công cuộc xây dựng của một số nước xã hội chủ nghĩa. Nên Đảng Bôn sê Vích thực hiện chính sách kinh tế mới 3-1921.</p><img class=\"img-responsive\" src=\"public/images/noidung/bai26/Ap-phich-nam-1921-World-History.jpg\"></img><blockquote>Hình 26.1: Áp phích năm 1921: “Chúng ta tuyên chiến với hậu quả của chiến tranh”.</blockquote><h4>b.Liên bang Xô viết thành lập</h4><p>Tháng 12/1922 Đại hội Xô viết toàn Nga đã tuyên bố thành lập Liên bang Cộng hòa xã hội chủ nghĩa Xô viết (Liên Xô).</p><p>Gồm 4 nước cộng hòa, 4 quốc gia đầu tiên là Nga, Ukraina, Bêlôruxia và Zakapkazơ (Azecbaijan, Acmênia, Gruzia), đến năm 1940 có thêm 11 nước.</p><img class=\"img-responsive\" src=\"public/images/noidung/bai26/Luoc-do-Lien-Xo-1940-World-History.jpg\"></img><blockquote>Hình 26.2: Lược đồ Liên Xô năm 1940.</blockquote><h2>2.Công cuộc xây dựng chủ nghĩa xã hội ở Liên Xô (1925 - 1941)</h2><h4>a.Những kế hoạch 5 năm đầu tiên và thành tựu</h4><p>Công nghiệp: thực hiện công nghiệp hóa xã hội chủ nghĩa</p><p>Sau công cuộc khôi phục kinh tế Liên Xô vẫn là một nước nông nghiệp lạc hậu. Kinh tế bị bao vây, kỹ thuật, thiết bị lệ thuộc nước ngoài</p><p>Đảng Cộng sản đề ra nhiệm vụ công nghiệp hóa xã hội chủ nghĩa.</p><p>Mục đích: đưa Liên Xô trở thành một nước công nghiệp có những ngành công nghiệp chủ chốt.</p><p>Biện pháp:</p><p>Ưu tiên phát triển công nghiệp nặng: công nghiệp chế tạo máy móc và nông cụ, công nghiệp năng lượng (điện, than, dầu mỏ), công nghiệp khai khoáng và công nghiệp quốc phòng</p><img class=\"img-responsive\" src=\"public/images/noidung/bai26/Nha-may-thuy-dien-Do-nhi-ep-World-History.jpg\"></img><blockquote>Hình 26.3: Nhà máy thủy điện Đơ-nhi-ép, khởi công năm 1927, là nhà máy thủy điện lớn nhất châu Âu thời đó, được đưa vào hoạt động tháng 10 - 1932.</blockquote><p>Có mục tiêu cụ thể cho từng kế hoạch dài hạn, kế hoạch năm năm lần thứ nhất 1928 - 1932) và kế hoạch năm năm lần thứ  hai (1933 - 1937).</p><p>Kết quả: Năm 1937 sản lượng công nghiệp chiếm 77,4% tổng sản phẩm quốc dân.</p><p>Nông nghiệp: ưu tiên tập thể hóa nông nghiệp, đưa 93% số nông hộ với 90% diện tích đất canh tác vào nền nông nghiệp tập thể hóa, có qui mô sản xuất lớn và cơ giới hoá.</p><p>Văn hóa - giáo dục: thanh toán nạn mù chữ, phát triển mạng lưới giáo dục phổ thông, phổ cập tiểu học trong cả nước, phổ cập trung học cơ sở thành phố.</p><p>Xã hội: cơ cấu giai cấp thay đổi xã hội chỉ còn 2 giai cấp lao động là công nhân, nông dân và trí thức xã hội chủ nghĩa.</p><p>Từ năm 1937 Liên Xô tiếp tục thực hiện kế hoạch 5 năm lần ba, sang tháng 6/1941, Đức tấn công Liên Xô, công cuộc xây dựng chủ nghĩa xã hội bị gián đoạn.</p><p>=>Mặc dù còn có những hạn chế song công cuộc xây dựng chủ nghĩa xã hội ở Liên Xô 1925 - 1941 vẫn đạt được những thành tựu to lớn, tạo nên những biến đổi nhiều mặt, có lợi cho nhân dân, xây dựng lại lực lượng vũ trang hùng mạnh để bảo vệ tổ quốc, giúp đỡ phong trào cách mạng thế giới</p><h4>b.Quan hệ ngoại giao của Liên Xô</h4><img class=\"img-responsive\" src=\"public/images/noidung/bai26/Quoc-huy-cua-Lien-Xo-World-History.png\"></img><blockquote>Hình 26.4: Quốc huy của Liên Xô.</blockquote><p>Liên Xô đã từng bước xác lập quan hệ ngoại giao với một số nước láng giềng châu Á, châu Âu.</p><p>Từng bước phá vỡ chính sách bao vây cô lập về kinh tế và ngoại giao của các nước đế quốc.</p><p>Bằng những biện pháp đấu tranh kiên quyết và mềm dẻo, chỉ trong vòng 4 năm (1922 - 1925)Liên Xô đã được các cường quốc tư bản: Đức, Anh, Italia, Pháp, Nhật, lần lượt công nhận và thiết lập quan hệ ngoại giao.</p><p>Thiết lập ngoại giao với 20 nước.</p><p>Năm 1933, Mĩ công nhận và thiết lập quan hệ ngoại giao với Liên Xô. Đó là thắng lợi lớn của nền ngoại giao Xô viết, khẳng định uy tín của Liên Xô trên trường quốc tế.</p></div>', 'public/images/noidung/bai26/Lien-xo-World-History.jpg', 3, NULL, '2018-04-06 17:00:00', NULL),
